#define AA  0   // (= 0/1) optional antialiasing

float PrBox2Df (vec2 p, vec2 b);
vec3 HsvToRgb (vec3 c);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
float SmoothMax (float a, float b, float r);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

const int nBall = 41;
vec3 bPos[nBall], pNeb[2], sunDir, qHit;
vec2 gId, gIdL, csFlor;
float tCur, dstFar, hbSize, wexSize, hInd, cHt, bRad, vFac, iQuad, hFac;
int nFrame, idObj, ballNum;
const int idPil = 1, idWal = 2, idRamp = 3, idPyr = 4, idWalEx = 5, idGrnd = 6, idBall = 10;
const float pi = 3.1415927;

#define VAR_ZERO min (nFrame, 0)
#define CosSin(x) (sin ((x) + vec2 (0.5 * pi, 0.)))
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

int HilXy2D (ivec2 p, int n)
{ // (Hilbert indexing from "Hilbert's Path" - with bit ops)
  ivec2 r;
  int j, td, d;
  d = 0;
  td = 1;
  for (j = n >> 1; j > 1; j >>= 1) td <<= 1;
  for (j = n >> 1; j >= 1; j >>= 1) {
    r = (p / td) & 1;
    d += j * j * ((r.y == 0) ? 3 * r.x : (3 * r.x) ^ 1);
    if (r.y == 0) {
      if (r.x != 0) p = n - 1 - p;
      p = p.yx;
    }
    td >>= 1;
  }
  return d;
}

ivec2 HilD2Xy (int d, int n)
{
  ivec2 p, r;
  int j, td;
  td = d;
  p = ivec2 (0);
  for (j = 1; j < n; j <<= 1) {
    r.x = (td >> 1) & 1;
    r.y = ((r.x == 0) ? td : td ^ 1) & 1;
    if (r.y == 0) {
      if (r.x != 0) p = j - 1 - p;
      p = p.yx;
    }
    p += j * r;
    td >>= 2;
  }
  return p;
}

#define HT(v) 4. * hFac * (1. - Maxv2 (abs ((v) / hbSize - 1.)))

void SetGParm ()
{
  vec2 gIdLL, g;
  float bb, iq, hi;
  int ic, ib;
  ib = int (hbSize);
  gIdL = gId;
  iQuad = step (hbSize, gIdL.x) + 2. * step (hbSize, gIdL.y);
  if (gIdL.x >= hbSize) gIdL.x -= hbSize;
  if (gIdL.y >= hbSize) gIdL.y -= hbSize;
  gIdLL = gIdL;
  if (iQuad < 2.) gIdLL.y = hbSize - 1. - gIdLL.y;
  ic = HilXy2D (ivec2 (gIdLL), ib);
  hInd = float (ic) + iQuad * hbSize * hbSize;
  if (iQuad < 2.) hInd = 2. * hbSize * hbSize - 1. - hInd;
  cHt = HT (gId + 0.5);
  bb = hbSize * hbSize;
  for (int k = 0; k < 2; k ++) {
    hi = mod (hInd + 1e-3 + sign (float (k) - 0.5), 4. * bb);
    iq = step (bb, abs (hi - 2. * bb)) + 2. * step (2. * bb, hi);
    ic = int (mod (mod (hi, bb) * ((iq == 0. || iq == 3.) ? 1. : -1.), bb));
    g = (vec2 (HilD2Xy (ic, ib)) + 0.5) * vec2 ((iq == 1. || iq == 3.) ? 1. : -1.,
       (iq == 2. || iq == 3.) ? 1. : -1.);
    pNeb[k].xz = g - (gId + 0.5) + hbSize;
    pNeb[k].y = HT (pNeb[k].xz + gId + 0.5);
  }
}

float GObjDf (vec3 p)
{
  vec3 q, qq, ds;
  float dMin, d, h, hf, sx;
  dMin = dstFar;
  p.y -= 0.7;
  if (Minv2 (gIdL) >= 0. && Maxv2 (gIdL) < hbSize) {
    q = p;
    hf = dot (vec2 (q.y, - Maxv2 (abs (q.xz - hbSize)) + hbSize), csFlor) + 0.3;
    d = abs (hf) - 0.05;
    DMINQ (idPyr);
    q = p;
    q.xz -= hbSize * vec2 (step (abs (abs (iQuad - 2.) - 1.), 0.), step (2., iQuad)) + gIdL + 0.5;
    if (iQuad < 2.) q.z *= -1.;
    d = max (SmoothMax (PrBox2Df (q.xz, vec2 (0.2)), q.y - cHt - 0.2, 0.01), - hf);
    DMINQ (idPil);
    qq = q;
    qq.y -= 0.07;
    q = qq;
    d = min (PrBox2Df (q.xz - 0.5 * pNeb[0].xz * vec2 (1., sign (iQuad - 1.5)), vec2 (0.2)),
       PrBox2Df (q.xz - 0.5 * pNeb[1].xz * vec2 (1., sign (iQuad - 1.5)), vec2 (0.2)));
    d = max (SmoothMax (hf - 0.2, SmoothMax (- PrBox2Df (q.xz, vec2 (0.45)), - d, 0.01), 0.01), - hf);
    DMINQ (idWal);
    for (int k = 0; k < 2; k ++) {
      ds = vec3 (pNeb[k].xz * vec2 (1., sign (iQuad - 1.5)), pNeb[k].y + cHt + 0.05).xzy;
      q = qq - 0.5 * ds;
      q.xz = mix (q.xz, q.zx, step (abs (ds.z), abs (ds.x)));
      sx = (abs (ds.x) > abs (ds.z)) ? sign (ds.x) : sign (ds.z);
      d = SmoothMax (PrBox2Df (q.xz, vec2 (0.1, 0.3)), - dot (q.yz, CosSin (atan (pNeb[k].y - cHt,
         -0.55 * sx))) * sx - 0.01, 0.01);
      d = max (SmoothMax (d, 0.23 - length (vec2 (abs (q.z) + 0.12, max (q.y + 0.4, 0.))),
         0.01), - hf);
      DMINQ (idRamp);
    }
  }
  return dMin;
}

float GObjRay (vec3 ro, vec3 rd)
{
  vec3 p, rdi;
  float dHit, d, s, sb, eps;
  bool cNu;
  if (rd.x == 0.) rd.x = 1e-4;
  if (rd.z == 0.) rd.z = 1e-4;
  rdi.xz = 1. / rd.xz;
  eps = 0.002;
  cNu = true;
  dHit = eps;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + dHit * rd;
    if (cNu) {
      gId = floor (p.xz);
      sb = Minv2 ((gId + step (0., rd.xz) - ro.xz) * rdi.xz);
      SetGParm ();
      cNu = false;
    }
    d = GObjDf (p);
    s = max (dHit, sb);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      cNu = true;
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 GObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = 2e-4 * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = GObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float GObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  gIdP = vec2 (-999.);
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    gId = floor (p.xz);
    if (gId != gIdP) {
      gIdP = gId;
      SetGParm ();
    }
    h = GObjDf (p);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p - vec3 (vec2 (hbSize), 0.15).xzy;
  q.xz = mix (q.xz, q.zx, step (abs (q.z), abs (q.x)));
  d = SmoothMax (PrBox2Df (vec2 (q.x, abs (q.z) - (hbSize + 0.1)), vec2 (hbSize + 4.1, 0.1)),
     abs (q.y) - 0.35, 0.02);
  DMINQ (idWalEx);
  q.y -= 0.65;
  d = SmoothMax (PrBox2Df (vec2 (q.x, abs (q.z) - wexSize), vec2 (wexSize + 0.2, 0.2)),
     abs (q.y) - 1., 0.02);
  if (abs (q.x) < wexSize - 0.5) d = SmoothMax (d, min (0.5 - length (vec2 (abs (fract (q.x +
     0.5) - 0.5) + 0.2, max (q.y - 0.2, 0.))), q.y + 0.7), 0.02);
  DMINQ (idWalEx);
  q = p;
  q.xz -= vec2 (hbSize);
  d = q.y;
  DMINQ (idGrnd);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = 2e-4 * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

void BPos ()
{  // (from "Looping Hilbertian Saltation")
  vec3 bp[2];
  vec2 g;
  float tc, t, bb, iq;
  int ib, ic;
  ib = int (hbSize);
  bb = hbSize * hbSize;
  for (int k = VAR_ZERO; k < nBall; k ++) {
    tc = 1e-3 + vFac * tCur + (float (k) / float (nBall)) * 4. * bb;
    for (int j = 0; j < 2; j ++) {
      t = mod (tc + float (j), 4. * bb);
      iq = step (bb, abs (t - 2. * bb)) + 2. * step (2. * bb, t);
      ic = int (mod (mod (t, bb) * ((iq == 0. || iq == 3.) ? 1. : -1.), bb));
      g = (vec2 (HilD2Xy (ic, ib)) + 0.5) * vec2 ((iq == 1. || iq == 3.) ? 1. : -1.,
         (iq == 2. || iq == 3.) ? 1. : -1.);
      bp[j].xz = g + hbSize;
      bp[j].y = HT (bp[j].xz) + 0.7;
    }
    t = clamp (1.3 * fract (tc) - 0.15, 0., 1.);
    bPos[k] = mix (bp[0], bp[1], t);
    bPos[k].y += bRad + 0.2 + 2. * t * (1. - t);
  }
}

vec4 BallHit (vec3 ro, vec3 rd)
{
  vec3 u, vn;
  float dMin, d, b, w;
  dMin = dstFar;
  for (int k = VAR_ZERO; k < nBall; k ++) {
    u = ro - bPos[k];
    b = dot (rd, u);
    w = b * b - dot (u, u) + bRad * bRad;
    if (w > 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) {
        dMin = d;
        vn = (u + d * rd) / bRad;
        ballNum = k;
      }
    }
  }
  return vec4 (dMin, vn);
}

float BallHitSh (vec3 ro, vec3 rd, float rng)
{
  vec3 u;
  float b, d, w, dMin;
  dMin = dstFar;
  for (int k = VAR_ZERO; k < nBall; k ++) {
    u = ro - bPos[k];
    b = dot (rd, u);
    w = b * b - dot (u, u) + bRad * bRad;
    if (w > 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) dMin = d;
    }
  }
  return 0.5 + 0.5 * smoothstep (0., rng, dMin);
}

float BlkHitSil (vec3 ro, vec3 rd, vec3 sz)
{
  vec3 v, tm, tp;
  float dn, df;
  v = ro / rd;
  tp = sz / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  return (df > 0. && dn < df) ? dn : dstFar;
}

vec3 BallCol (int k)
{
  return HsvToRgb (vec3 (fract (4. * float (k) / float (nBall)), 0.7, 1.));
}

float GrndHt (vec2 p)
{
  float s, h;
  s = PrBox2Df (qHit.xz, vec2 (wexSize));
  h = (s < 0.) ? 0.03 * (0.5 - 0.5 * sin (8. * pi * s)) :  0.2 * Fbm2 (0.05 * p);
  return h;
}

vec3 GrndNf (vec3 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return normalize (vec3 (GrndHt (p.xz) - vec2 (GrndHt (p.xz + e.xy),
     GrndHt (p.xz + e.yx)), e.x).xzy);
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd;
  q = 0.005 * (ro.xz + tCur * vec2 (0.5, 2.) + ((200. - ro.y) / rd.y) * rd.xz);
  ff = Fbm2 (q);
  f = smoothstep (0.1, 0.8, ff);
  fd = smoothstep (0.1, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
  clCol = 0.7 * (0.8 + 0.5 * ff) * (vec3 (1.) - vec3 (0.3, 0.3, 0.2) * sign (fd) *
     smoothstep (0., 0.05, abs (fd)));
  sd = max (dot (rd, sunDir), 0.);
  fd = smoothstep (0.01, 0.1, rd.y);
  skCol = mix (vec3 (0.8, 0.8, 0.75), vec3 (0.3, 0.4, 0.7), 0.3 + 0.7 * fd) +
     step (0.1, sd) * vec3 (1., 1., 0.9) * min (0.3 * pow (sd, 64.) + 0.5 * pow (sd, 2048.), 1.);
  col = mix (skCol, clCol, 0.1 + 0.9 * f * fd);
  return 0.9 * col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, db4;
  vec3 col, vn, qHitG;
  vec2 b, vf;
  float dstObj, dstObjG, db, s, sh;
  int idObjG;
  hFac = (hbSize == 8.) ? 1.5 : 1.;
  csFlor = CosSin (- ((hbSize == 4.) ? 0.24 : 0.2) * pi);
  vFac = 0.5;
  bRad = 0.2;
  BPos ();
  db = BlkHitSil (ro - vec3 (vec2 (hbSize), 2.5 * hFac + 0.7).xzy, rd, vec3 (vec2 (hbSize + 0.2),
     2.7 * hFac).xzy);
  if (db < dstFar) {
    dstObjG = GObjRay (ro, rd);
    idObjG = idObj;
    qHitG = qHit;
    db4 = BallHit (ro, rd);
  } else {
    dstObjG = dstFar;
    db4.x = dstFar;
  }
  dstObj = ObjRay (ro, rd);
  if (min (db4.x, dstObjG)  < min (dstObj, dstFar)) {
    if (db4.x < dstObjG) {
      dstObj = db4.x;
      idObj = idBall + ballNum;
    } else {
      dstObj = dstObjG;
      idObj = idObjG;
      qHit = qHitG;
    }
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    if (dstObj == dstObjG) {
      vn = GObjNf (ro);
    } else if (idObj < idBall) {
      vn = (idObj == idGrnd) ? GrndNf (ro) : ObjNf (ro);
    } else {
      vn = db4.yzw;
    }
    vf = vec2 (0.);
    if (idObj == idPil) {
      col4 = vec4 (1., 1., 1., 0.1);
      s = length (qHit.xz);
      if (s < 0.2) col4 *= 0.93 + 0.07 * sin (32. * pi * s);
    } else if (idObj == idRamp) {
      col4 = vec4 (0.95, 1., 0.95, 0.1);
      if (vn.y > 0.99) col4 *= 0.8 + 0.2 * smoothstep (0.07, 0.1, abs (fract (16. * qHit.z) - 0.5));
      else if (vn.y > 0.1) col4 *= 0.8 + 0.2 * smoothstep (0.07, 0.1, abs (fract (16. * qHit.y) - 0.5));
    } else if (idObj == idWal) {
      col4 = 0.9 * vec4 (1., 0.95, 0.95, 0.1);
    } else if (idObj == idWalEx) {
      col4 = 0.9 * vec4 (1., 0.95, 0.95, 0.1);
      vf = vec2 (16., 0.5);
    } else if (idObj == idPyr) {
      col4 = 0.8 * vec4 (1., 1., 0.9, 0.1);
      vf = vec2 (32., 0.3);
    } else if (idObj == idGrnd) {
      col4 = 0.9 * vec4 (1., 1., 0.8, 0.);
      s = PrBox2Df (qHit.xz, vec2 (wexSize));
      col4 *= 1. - 0.07 * ((s < 0.) ? (0.5 - 0.5 * sin (8. * pi * s)) : Fbm2 (0.05 * ro.xz));
      vf = vec2 (8., 0.5);
      col4 *= 1. - 0.1 * Fbm2 (16. * ro.xz);
    } else if (idObj >= idBall) {
      col4 = vec4 (BallCol (idObj - idBall), -1.);
    }
    if (vf.y > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    if (col4.a >= 0.) {
      sh = min (min (GObjSShadow (ro + 0.01 * vn, sunDir), ObjSShadow (ro + 0.01 * vn, sunDir)),
         BallHitSh (ro + 0.01 * vn, sunDir, 8.));
      col = col4.rgb * (0.15 + 0.2 * max (dot (vn, sunDir * vec3 (-1., 1., -1.)), 0.) +
         0.7 * sh * max (dot (vn, sunDir), 0.)) +
        col4.a * step (0.95, sh) * pow (max (dot (sunDir, reflect (rd, vn)), 0.), 32.);
    } else col = col4.rgb * (0.4 + 0.6 * max (- dot (vn, rd), 0.));
  } else if (rd.y < 0.) {
    ro += (- ro.y / rd.y) * rd;
    col = 0.9 * vec3 (1., 1., 0.8) * (0.15 + 0.7 * sunDir.y);
  } else {
    col = SkyCol (ro, rd);
  }
  if (dstObj < dstFar || rd.y < 0.) {
    col *= 1. - 0.15 * smoothstep (0.5, 0.7, Fbm2 (0.05 * ro.xz - 0.05 * tCur));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  zmFac = 5.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az = 2. * pi * mPtr.x;
    zmFac += smoothstep (0.2, 0.5, abs (az)) * min (4. * abs (az), 4.);
    el = pi * mPtr.y;
  }
  el -= 0.12 * pi;
  el = clamp (el, -0.5 * pi, -0.03 * pi);
  vuMat = StdVuMat (el, az);
  hbSize = 8.; //4.;
  wexSize = hbSize + 4.;
  ro = vuMat * vec3 (0., 0.5, -6. * hbSize);
  ro.xz += hbSize;
  ro.y = max (ro.y, 0.1);
  dstFar = 300.;
  sunDir = normalize (vec3 (0.8, 0.9, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (mod (dot (p, cHashVA2) + vec2 (0., cHashVA2.x), 2. * pi)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

