vec3 light(vec3 p, vec3 rd, vec3 n, vec3 lightPos, vec3 lightCol) {

    float radius = .003;
    float l = length(lightPos - p);
    float ndotl = max(0., dot(n, normalize(lightPos - p)) );
    return lightCol * ndotl / max(1., l*l*radius);
}

