bool keydown(int key) {
    return (texelFetch(iChannel3, ivec2(key,0), 0).x >= 0.5);
}


// a smooth maximum of all the channels down to a scalar
// which we need to form a smooth intersection of two
// channels so we don't get dFdx() artefacts.
float foldheight(vec4 x) {
    const float k = 8.0;
    return log2(dot(exp2(x*k), vec4(1))) / k;
}
// an attempt at blending two different alpha equations
float smoothmin(float x, float y) {
    const float k = -8.0;
    return log2(exp2(x*k) + exp2(y*k)) / k;
}
vec4 meniscus(vec4 x, vec2 uv) {
    float height = foldheight(x) * 3.0;
    height = smoothclamp(height, -1.0, 1.0);
    // Shade the edge according to the direction it's facing.
    float shade = cos(atan(dFdy(height), dFdx(height)) + 1.1) * 0.5 + 0.5;
    // Alpha should be derived from slope, but that's quarter resolution,
    // so height looks a bit smoother.
    float alpha = min(1.0 - height, 1.0);

    // Except when a peninsula isn't high enough to zero alpha, in which case
    // we need to tamp it down some more based on slope rather than absolute
    // height.
    float squish = length(vec2(dFdx(height), dFdy(height)) / fwidth(uv));
    squish = clamp(squish * 0.05, 0.0, 1.0);
    alpha = smoothmin(alpha, squish);
    // ... but it doesn't really work.

    return clamp(vec4(vec3(shade), alpha), 0.0, 1.0);
}


vec4 pal16(vec4 x) {
    // Convert saturated (ie., approximately binary) xyzw values
    // into a 16-colour palette.  In this case, an approximation
    // of the C64 colour palette.  Why the C64?  I do not know.
    // I just wanted to do away with that RGB-filter look that
    // comes out otherwise.
    vec3 lut[16] = vec3[16](
        vec3(0., 0., 0.), vec3(1., 1., 1.), vec3(0., 1., 0.), vec3(.5, 0., 0.),
        vec3(.6, 1., .9), vec3(.7, .3, .7), vec3(0., .7, .4), vec3(0., 0., .6),
        vec3(.9, .9, .5), vec3(.8, .5, .3), vec3(1., .5, .5), vec3(.2, .2, .2),
        vec3(.5, .5, .5), vec3(.6, 1., .7), vec3(0., .5, 1.), vec3(.8, .8, .8));
     vec3 colour = vec3(0);
     for (int i = 1; i < 16; ++i) {
         float w = 1.0;
         for (int j = 0; j < 4; ++j) {
             w *= (i >> j & 1) != 0 ? x[j] : 1.0 - x[j];
         }
         colour += lut[i] * w;
     }
     return vec4(colour, 1.0);
}


void mainImage(out vec4 fragColour, in vec2 fragCoord) {
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.x + 0.5;

    // Read the blurred feedback buffer, using bicubic interpolation
    // to avoid scaling artefacts, and threshold it into a flat puddle
    // of colour.
    vec4 threshold = texture(iChannel1, 1.0 - 0.5 / iChannelResolution[1].xy);
    vec4 colour = bicubictex(iChannel0, uv * dimension / iChannelResolution[0].xy,
                             iChannelResolution[0].xy);
    // Calculate a shading for the edge for a little bit of depth.
    vec4 meniscus = meniscus(colour - threshold, uv);
    // threshold, with anti-aliasing
    colour = aastep(threshold, colour);

    // debug stuff
    if (keydown(49)) colour = texture(iChannel1, uv * dimension / iChannelResolution[1].xy);
    if (keydown(50)) colour = texture(iChannel0, uv * dimension / iChannelResolution[0].xy);
    if (keydown(51)) colour = cubetex(iChannel2, uv * dimension / iChannelResolution[2].xy, 0);
    if (keydown(52)) colour = cubetex(iChannel2, uv * dimension / 16.0 / iChannelResolution[2].xy, 1);
    if (keydown(53)) colour = cubetex(iChannel2, uv * dimension / 256.0 / iChannelResolution[2].xy, 2);
    if (keydown(54)) colour = cubetex(iChannel2, uv * dimension / iChannelResolution[2].xy, 3);
    if (keydown(55)) colour = cubetex(iChannel2, uv * dimension / iChannelResolution[2].xy, 4);
    if (keydown(56)) colour = cubetex(iChannel2, uv * dimension / iChannelResolution[2].xy, 4);
    if (keydown(57)) colour = cubetex(iChannel2, uv * dimension / iChannelResolution[2].xy, 4);
    if (keydown(48) && uv.x > 0.90) {
        vec4 mask = 1.0 - smoothstep(0.40, 0.42, abs((uv.x - 0.9) * 40.0 - vec4(0.5,1.5,2.5,3.5)));
        colour = mask * smoothstep(-0.01, 0.01, threshold * iResolution.y - fragCoord.y);
        meniscus = vec4(0);
    }
    // zero out the meniscus shading if there's nothing to shade
    meniscus *= max(max(colour.x, colour.y), max(colour.z, colour.w));

    colour = pal16(colour);
    if (!keydown(16)) colour = mix(colour, meniscus, meniscus.a);
    fragColour = colour;
}

