// size of one edge of the feedback buffer
const float dimension = 128.0;

// target ratio of full versus empty pixels
const float setpoint = 0.12;

// how blurry to make things
const float blur_radius = (0.25 * dimension);

// filter size for blurring
const int blur_taps = 11;

// everybody's favourite constant
const float tau = 6.28318530718;

// anti-alias a threshold operation based on the local gradient
float aastep(float threshold, float value) {
    float delta = 0.7 * fwidth(value);
    return smoothstep(-delta, delta, value - threshold);
}
vec4 aastep(vec4 threshold, vec4 value) {
    vec4 delta = 0.7 * fwidth(value);
    return smoothstep(-delta, delta, value - threshold);
}

// clamp coordinate to centre of edge pixel rather than edge of texture,
// to avoid interpolating into unused data
vec2 pixelclamp(vec2 uv) {
    const float halfpix = 0.5 / dimension;
    return clamp(uv, halfpix, 1.0 - halfpix);
}

// smoothstep, but with result between lo and hi rather than 0 and 1
float smoothclamp(float x, float lo, float hi) {
    return lo + smoothstep(lo, hi, x) * (hi - lo);
}
vec4 smoothclamp(vec4 x, float lo, float hi) {
    return lo + smoothstep(lo, hi, x) * (hi - lo);
}

// convert texture coordinate and face into cubemap direction
vec3 cubedir(vec2 uv, int face) {
    vec3 dir = vec3(uv * 2.0 - 1.0, 1.0);
    dir.y = -dir.y;
    switch (face) {
    case 1: dir = vec3(-dir.x, dir.y,-dir.z); break;
    case 2: dir = vec3( dir.x, dir.z,-dir.y); break;
    case 3: dir = vec3( dir.x,-dir.z, dir.y); break;
    case 4: dir = vec3( dir.z, dir.y,-dir.x); break;
    case 5: dir = vec3(-dir.z, dir.y, dir.x); break;
    }
    return dir;
}
// texture from cubemap
vec4 cubetex(samplerCube tex, vec2 uv, int face) {
    return texture(tex, cubedir(uv, face));
}
vec4 cubetexLod(samplerCube tex, vec2 uv, int face, float lod) {
    return textureLod(tex, cubedir(uv, face), lod);
}

// the four weight coefficients for cubic interpolation
vec2 w0(vec2 a) { return a * (a * (-a + 3.0) - 3.0) + 1.0; }
vec2 w1(vec2 a) { return a * a * (3.0 * a - 6.0) + 4.0; }
vec2 w2(vec2 a) { return a * (a * (-3.0 * a + 3.0) + 3.0) + 1.0; }
vec2 w3(vec2 a) { return a * a * a; }
// and a bicubic sampler using four linear interpolated samples
// and the above weights
vec4 bicubictex(sampler2D tex, vec2 uv, vec2 dims) {
	uv = uv * dims + 0.5;
	vec2 fuv = fract(uv);
    uv = floor(uv) - 0.5;

    vec2 g0 = w0(fuv) + w1(fuv);
    vec2 g1 = w2(fuv) + w3(fuv);
    vec2 h00 = w1(fuv) / g0 - 1.0;
    vec2 h11 = w3(fuv) / g1 + 1.0;
    vec2 h10 = vec2(h11.x, h00.y);
    vec2 h01 = vec2(h00.x, h11.y);

    vec2 s = 1.0 / dims;
    return ((texture(tex, (uv + h00) * s) * g0.x
           + texture(tex, (uv + h10) * s) * g1.x) * g0.y
          + (texture(tex, (uv + h01) * s) * g0.x
           + texture(tex, (uv + h11) * s) * g1.x) * g1.y) / 36.0;
}
vec4 bicubiccubetex(samplerCube tex, vec2 uv, int face) {
	uv = uv * 1024.0 + 0.5;
	vec2 fuv = fract(uv);
    uv = floor(uv) - 0.5;

    vec2 g0 = w0(fuv) + w1(fuv);
    vec2 g1 = w2(fuv) + w3(fuv);
    vec2 h00 = w1(fuv) / g0 - 1.0;
    vec2 h11 = w3(fuv) / g1 + 1.0;
    vec2 h10 = vec2(h11.x, h00.y);
    vec2 h01 = vec2(h00.x, h11.y);

    const vec2 s = 1.0 / vec2(1024.0);
    return ((cubetex(tex, (uv + h00) * s, face) * g0.x
           + cubetex(tex, (uv + h10) * s, face) * g1.x) * g0.y
          + (cubetex(tex, (uv + h01) * s, face) * g0.x
           + cubetex(tex, (uv + h11) * s, face) * g1.x) * g1.y) / 36.0;
}

// gaussian blur function
float pdf(float x) {
#if 0
    // pdf for s=0.1667: `sum(uniform(-.5,.5, 3)/3)`
    return 2.39365*exp(-18. * x * x);
#else
    // pdf for s=0.144: `sum(uniform(-.5,.5, 4)/4)`
    return 2.77043 * exp(-24.1126 * x * x);
#endif
}
vec4 gausstex(sampler2D tex, vec2 uv, vec2 dir) {
    vec2 lo = 0.5 * abs(dir);
    vec2 hi = dimension * abs(dir) - lo;
    hi = mix(hi, vec2(1), equal(dir, vec2(0)));
    vec4 sum = vec4(0);
    float den = 0.0;
    for (int i = -blur_taps; i <= blur_taps; i++) {
        float f = float(i);
        float w = pdf(f / blur_radius);
        vec2 p = clamp(uv + dir * f, lo, hi);
        sum += texture(tex, p) * w;
        den += w;
    }
    return sum / den;
}

