// something interesting to get when the texture coordinate goes
// out of range (this keeps the buffer seeded even when it might
// become empty.
vec4 outfill(vec2 uv, float t) {
    uv -= 0.5;
    vec4 c = cos(atan(uv.y, uv.x) + t * vec4(-0.3,0.4,-0.5,0.6)) * 0.3;
    return clamp(c, 0.0, 1.0);
}

// sample from a buffer or the outfill value if out of bounds
vec4 edgefill(sampler2D tex, vec2 uv, vec4 outfill) {
    if (pixelclamp(uv) != uv) return outfill;
    return texture(tex, uv* dimension / iChannelResolution[0].xy);
}
vec4 edgefill(samplerCube tex, vec2 uv, int face, vec4 outer) {
    if (pixelclamp(uv) != uv) return outer;
    return cubetex(tex, uv * dimension / 1024.0, face);
}

#define cs(a) vec2(cos(a), sin(a))
#define csmat(a) mat2x2(cos(a), -sin(a), sin(a), cos(a))

// swish coordinates around a bit for feedback
vec2 warp0(vec2 uv, float t) {
    const mat2x2 turn = csmat(0.001) * 1.001;
    vec2 wobble = cos(uv.yx * vec2(16,15) + t) * 0.0018;
    vec2 o = 0.5 + cs(t * 1.3) * .25;
    uv = (uv - o) * turn + o; 
    return uv + wobble;
}

// swish, but differently
vec2 warp1(vec2 uv, float t) {
    const mat2x2 turn = csmat(0.001) * 0.999;
    vec2 wobble = cos(uv.yx * vec2(17,16) + t) * 0.0018;
    vec2 o = 0.5 - cs(t * 0.76) * .25;
    uv = (uv - o) * turn + o; 
    return uv + wobble;
}

// decide which swish function to use (or blend them together)
vec4 churn(vec2 uv, float t) {
    uv -= 0.5;
    vec4 ox = uv.x + cos(t * .4*vec4(0.5,-0.5, 0.4,0.3)) * 0.25;
    vec4 oy = uv.y + sin(t * .4*vec4(0.4, 0.4,-0.3,0.4)) * 0.25;

    vec4 t4 = t * vec4(-0.15,0.24,0.23,-0.32);
    ox = ox * cos(t4) - oy * sin(t4);

    return smoothstep(-0.15, 0.15, ox);
}

vec4 updatethreshold(vec4 threshold) {
    // we keep a threshold value in one corner of buffer A,
    // and depending on the number of set pixels we get in
    // the cubemap we increment or decrement it to try to
    // get to the setpoint.
#if 0 // need to set mipmaps on ichannel1 for this version.
    const float mmfix = (1024.0 * 1024.0) / (dimension * dimension);
    vec4 median = cubetexLod(iChannel1, vec2(0.5), 0, 99.9) * mmfix;
#else
    vec4 median = vec4(0);
    float divisor = 0.0;
    for (int i = 0; i < int(dimension) / 16; ++i) {
        for (int j = 0; j < int(dimension) / 16; ++j) {
            vec2 o = vec2(i, j);
            median += cubetex(iChannel1, o / 1024.0, 1);
            divisor += 1.0;
        }
    }
    median /= divisor;
#endif
    vec4 adj = smoothclamp(median - setpoint, -0.15, 0.15);
    return threshold + adj * 0.125;
}

void mainImage(out vec4 fragColour, in vec2 fragCoord) {
    if (all(greaterThan(fragCoord, iResolution.xy - 1.0))) {
        vec2 tc = 1.0 - 0.5 / iResolution.xy;
        vec4 threshold = texture(iChannel0, tc);
        threshold = updatethreshold(threshold);
        if (iFrame == 0) threshold = vec4(0.5);
        fragColour = clamp(threshold, 0.01, 0.99);
        return;
    }
    if (max(fragCoord.x, fragCoord.y) > dimension) discard;
    vec2 uv = fragCoord / dimension;

    vec4 outer = outfill(uv, iTime);
    vec4 churn = churn(uv, iTime);
    vec2 uv0 = warp0(uv, iTime);
    vec2 uv1 = warp1(uv, iTime);
    vec4 colour = mix(edgefill(iChannel0, uv0, outer),
                      edgefill(iChannel0, uv1, outer),
                      churn);
    vec4 quant = mix(edgefill(iChannel1, uv0, 0, outer),
                     edgefill(iChannel1, uv1, 0, outer),
                     churn);
    // blend a little of the quantised buffer into the feedback
    // buffer to gently encourage it towards wider dynamic range.
    colour = mix(colour, quant, 0.01);

    // where colours overlap, encourage them to separate a bit.
    vec4 repel = colour.yzwx + colour.zwxy + colour.wxyz * 1.5;
    colour -= repel * 0.0003;

    fragColour = clamp(colour, 0.0, 1.0);
}

