// figure out which cube face we're rendering
int cubeface(vec3 dir) {
    vec3 adir = abs(dir);
    vec3 m = vec3(greaterThan(adir, max(adir.yzx, adir.zxy)));
    return int(dot(m, vec3(4, 2, 0)))
        +  int(dot(m, dir) < 0.0);
}

// Read blurred version of feedback buffer, and threshold
// it to give a count of 'set' pixels which can be read
// from the mipmap.
void mainCubemap(out vec4 fragColour, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir) {
    int f = cubeface(rayDir);
    fragColour = vec4(0);
    if (f == 0) {
        if (max(fragCoord.x, fragCoord.y) > dimension) discard;
        vec2 s0 = 1.0 / iChannelResolution[0].xy;
        vec2 s1 = 1.0 / iChannelResolution[1].xy;

        vec4 threshold = texture(iChannel1, 1.0 - 0.5 * s1);
        vec4 colour = texture(iChannel0, fragCoord * s0);

        fragColour = smoothstep(-0.125, 0.125, colour - threshold);
    } else {
        float scale = float(1 << (f * 4));
        fragCoord = (fragCoord - 0.5) * scale + 0.5;
        if (max(fragCoord.x, fragCoord.y) > dimension) discard;
        vec4 sum = vec4(0);
        for (int i = 0; i < 16; ++i) {
            for (int j = 0; j < 16; ++j) {
                vec2 uv = (fragCoord + vec2(i,j)) / iChannelResolution[2].xy;
                sum += cubetex(iChannel2, uv, f - 1);
            }
        }
        fragColour = sum / 256.0;
    }
}

