    float drawInterpolation = smoothstep(0.455, 0.65, animationTime);
    uv.x += smoothstep(0.55, 1.3, animationTime) * 22.5;
    float sampleRate = 0.8;
     uv.x*= sampleRate;
    float sinus = baseWaveFunc(uv.x);

    float spaceFloor = floor(uv.x);
    float spaceFrac  = fract(uv.x); 

    float sampleThis  = baseWaveFunc((spaceFloor +0.5     )) ;
    float sampleRight = baseWaveFunc((spaceFloor +0.5 + 1.)) ;
    float sampleLeft  = baseWaveFunc((spaceFloor +0.5 - 1.)) ;

    vec2 samplePoint = vec2(spaceFloor + 0.5,      sampleThis) ;

    float line = mix(sampleLeft, sampleThis, smoothstep(-0.5, 0.5, spaceFrac));
    line = mix(line, sampleRight, smoothstep(0.5, 1.5, spaceFrac));

    col = mix(vec3(1.,0.86,0.), col,min(1.,smoothstep(lineSize, lineSize + smoothing, 
     abs(sinus - uv.y) ) + smoothstep(0.455, 0.475, animationTime) ) );

        col = mix(col, vec3(0.,0.3,1.), 
        smoothstep(0.12, 0.1, length((samplePoint-  uv)   * vec2(1., sampleRate)) ));



    col = mix(vec3(0., 1., 0.), col,min(1., smoothstep(0.0, 0.1, max(0., uv.x - ( -5. + drawInterpolation* 20.)))
    + smoothstep(0.01,0.02, abs(uv.y - line)))); 

    // Output to screen
    fragColor = vec4(col,1.0);
