// loopless + golfing 533 "Sphere fake 3d" by SnackEater. https://shadertoy.com/view/dljfWW
// 272 without Jacobians -  288 with approx J  - 308 with exact J

#define VISUALIZE_XY_Y_ALSO   0

vec4 hsb2rgb( in vec4 c )
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return vec4(c.z * mix( vec3(1.0), rgb, c.y), 1.);
}


#define ROT mat2(cos( iTime +vec4(.5,33,11,0.)))

