#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, vec2 U) 

#define density 1.5
#define gravity 40.

#define K .004*min(float(iFrame)/60.,60.)

#define T0 2.6



float volcano (vec2 U, vec2 r, out vec4 Q) {

    float x = (U.x-.5*r.x)/r.x;

    float y = .25+exp(-20.*(sqrt(abs(x))*abs(x)))-
             .01*sin(x*10.+19.*sin(x*32.)+12.*sin(3.*x))-
             .05*sin(x*3.+2.*sin(x*22.)+1.*sin(13.*x))-
             .9*exp(-pow(abs(x)*200.,12.));
    vec2 v = U-vec2(.5,.04)*r;
    float m = -sign(length(v)-.09*r.y-r.y*.1*atan(10.5*sin(atan(v.y,v.x))));
    float h = sign(U.y-.6*r.y*y);
    if (h >= m) {
        Q = vec4(0);
        return h;
    } else {
        Q = vec4(0,0,T0,7.);
        return m;
    }

}

