// Distance to a section of a ring.
//
// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and https://iquilezles.org/articles/distfunctions2d


float sdRing( in vec2 p, in vec2 n, in float r, in float th )
{
    p.x = abs(p.x);

    p = mat2(n.x,n.y,-n.y,n.x)*p;

    return max( abs(length(p)-r)-th*0.5,
                length(vec2(p.x,max(0.0,abs(r-p.y)-th*0.5)))*sign(p.x) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    // animation
    float t = 3.14159*(0.5+0.5*cos(iTime*0.52));
    vec2 cs = vec2(cos(t),sin(t));
    const float ra = 0.5;
    const float th = 0.2;

    // distance
    float d = sdRing(p, cs, ra, th);

    // coloring
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
	col *= 1.0 - exp2(-25.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*abs(d));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    // mouse interaction
    if( iMouse.z>0.001 )
    {
    d = sdRing(m, cs, ra, th);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col, 1.0);
}

const float max_dist = 10.0;


float to_toroid_sdf(in vec3 pt) {
  float to_toroid_spine = length(pt.xz) - 0.75;
  float to_toroid = length(vec2(to_toroid_spine, pt.y)) - 0.25;
  return to_toroid;
}

float sdf(in vec3 pt) {
  float to_toroid_spine = length(pt.xz) - 0.75;
  float to_toroid = length(vec2(to_toroid_spine, pt.y)) - 0.25;
  float angle = atan(pt.x, pt.z);
  vec2 p1 = vec2(sin(0.5 * angle), cos(0.5 *angle));
  vec2 p2 = vec2(cos(0.5 * angle), -sin(0.5 *angle));
  float d1 = dot(vec2(to_toroid_spine, pt.y), p1);
  float d2 = dot(vec2(to_toroid_spine, pt.y), p2);
  float max_angle_dist = min(max(d1, d2), max(-d1, -d2));
  return max(to_toroid, max_angle_dist);
}

float sdf_other_side(in vec3 pt) {
  float to_toroid_spine = length(pt.xz) - 0.75;
  float to_toroid = length(vec2(to_toroid_spine, pt.y)) - 0.25;
  float angle = atan(pt.x, pt.z);
  vec2 p1 = vec2(sin(0.5 * angle), cos(0.5 *angle));
  vec2 p2 = vec2(cos(0.5 * angle), -sin(0.5 *angle));
  float d1 = dot(vec2(to_toroid_spine, pt.y), p1);
  float d2 = dot(vec2(to_toroid_spine, pt.y), p2);
  float max_angle_dist = min(max(-d1, d2), max(d1, -d2));
  return max(to_toroid, max_angle_dist);
}

float rough_raycast(in vec3 orig, in vec3 dir) {
  float accum = 0.0;
  float d = to_toroid_sdf(orig);
  for (int i = 0; i < 256; ++i) {
    accum = accum + 0.9 * d;
    vec3 pt = orig + accum * dir;
    d = to_toroid_sdf(pt);
    if (abs(d) < 1.0e-2) {
      return accum;
    }
    if (d > max_dist) {
      return max_dist + 1.0;
    }
  }
  return accum;
}


float raycast(in vec3 orig, in vec3 dir, float start_guess) {
  float accum = start_guess;
  if (accum > max_dist) {
    return accum;
  }
  float d = sdf(orig + accum * dir);
  for (int i = 0; i < 256; ++i) {
    accum = accum + 0.7 * d;
    vec3 pt = orig + accum * dir;
    d = sdf(pt);
    if (abs(d) < 1.0e-6) {
      return accum;
    }
    if (accum > max_dist) {
      return max_dist + 1.0;
    }
  }
  return accum;
}

float raycast_other_side(in vec3 orig, in vec3 dir, float start_guess) {
  float accum = start_guess;
  if (accum > max_dist) {
    return accum;
  }
  float d = sdf_other_side(orig + dir * accum);
  for (int i = 0; i < 256; ++i) {
    accum = accum + 0.7 * d;
    vec3 pt = orig + accum * dir;
    d = sdf_other_side(pt);
    if (d < 1.0e-6) {
      return accum;
    }
    if (d > max_dist) {
      return max_dist + 1.0;
    }
  }
  return accum;
}


vec3 get_norm(in vec3 pt) {
  float f = sdf(pt);
  const float h = 1.0e-3;
  return normalize(vec3(sdf(pt + vec3(h, 0.0, 0.0)) - f,
                        sdf(pt + vec3(0.0, h, 0.0)) - f,
                        sdf(pt + vec3(0.0, 0.0, h)) - f));
}

vec3 get_norm_other_side(in vec3 pt) {
  float f = sdf_other_side(pt);
  const float h = 1.0e-3;
  return normalize(vec3(sdf_other_side(pt + vec3(h, 0.0, 0.0)) - f,
                        sdf_other_side(pt + vec3(0.0, h, 0.0)) - f,
                        sdf_other_side(pt + vec3(0.0, 0.0, h)) - f));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float uv_scale = min(iResolution.x, iResolution.y);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / uv_scale;

    vec3 orig = vec3(0.0, 0.0, -1.5);
    vec3 ray = normalize(2.0 * vec3(uv, 0) - orig);

    float theta_tilt = 2.0 * iMouse.y / iResolution.y - 1.0;
    theta_tilt *= 3.141592654 * 0.5;
    float ct = cos(theta_tilt);
    float st = sin(theta_tilt);
    mat3 tilt = mat3(1.0, 0.0, 0.0,
                       0.0, ct, -st,
                       0.0, st, ct);

    float phi_yaw = 3.0 * 3.141592654 * (2.0 * iMouse.x / iResolution.x - 1.0);
    float cp = cos(phi_yaw);
    float sp = sin(phi_yaw);
    mat3 yaw = mat3(cp, 0.0, -sp,
                    0.0, 1.0, 0.0,
                    sp, 0.0, cp);
    orig = yaw * tilt * orig;
    ray = yaw * tilt * ray;

    float bright = 0.95;

    vec3 overlay = vec3(0.5);

    vec3 col = vec3(0.0);

    float start_guess = rough_raycast(orig, ray);

    float dist = raycast(orig, ray, start_guess);
    if (dist < max_dist) {
      overlay = vec3(0.0);
      vec3 n = get_norm(orig + dist * ray);
      // pixel color
      vec3 bounce = normalize(reflect(ray, n));
      col = smoothstep(vec3(0.0), vec3(1.0), texture(iChannel1, bounce).rgb) + 0.2 * bounce;
      bright = 1.0;
    } else {
      col = texture(iChannel0, normalize(ray)).rgb;
      bright = 0.5;
    }


    // Output to screen
    fragColor = vec4(bright * col + overlay,1.0);
}

const int MAXSTEP = 500;
const float MAXDIST = 200.0;
const float TOL = 0.001;
const float PI = 3.1415;

vec3 getTriplanarWeights ( in vec3 n ) 
{
	vec3 triW = abs(n);
	return triW / (triW.x + triW.y + triW.z);
}

mat2 rotate2d(float theta) {
  float s = sin(theta), c = cos(theta);
  return mat2(c, -s, s, c);
}

// Rotation matrix around the X axis.
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

// Rotation matrix around the Y axis.
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

// Rotation matrix around the Z axis.
mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

// Identity matrix.
mat3 identity() {
    return mat3(
        vec3(1, 0, 0),
        vec3(0, 1, 0),
        vec3(0, 0, 1)
    );
}

// Camera matrix.
mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 cd = normalize(cameraPos - lookAtPoint); // camera direction
	vec3 cr = normalize(cross(vec3(0, 1, 0), cd)); // camera right
	vec3 cu = normalize(cross(cd, cr)); // camera up

	return mat3(cr, cu, cd);
}

struct sdfCol
{
    float sdf;
    vec4 col;
};

float sdSphere( in vec3 p, in float r, in vec3 offset )
{
    return length( p - offset ) - r;
}

float sdFloor( in vec3 p )
{
    return p.y + 3.0;
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xy)-t.x, p.z);
  return length(q)-t.y;
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }

float length2( vec3 p ) { p=p*p; return sqrt(p.x+p.y+p.z); }
float length2( vec2 p ) { p=p*p; return sqrt(p.x+p.y); }

float length4( vec3 p ) { p=p*p; p=p*p; return pow(p.x+p.y+p.z, 1.0/4.0); }
float length4( vec2 p ) { p=p*p; p=p*p; return pow(p.x+p.y, 1.0/4.0); }

float length6( vec3 p ) { p=p*p*p; p=p*p; return pow(p.x+p.y+p.z, 1.0/6.0); }
float length6( vec2 p ) { p=p*p*p; p=p*p; return pow(p.x+p.y, 1.0/6.0); }

float length8( vec3 p ) { p=p*p; p=p*p; p=p*p; return pow(p.x+p.y+p.z, 1.0/8.0); }
float length8( vec2 p ) { p=p*p; p=p*p; p=p*p; return pow(p.x+p.y, 1.0/8.0); }

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length4(p.xz),p.y)) - vec2(r,h);
  return min(max(d.x,d.y),0.0) + length4(max(d,0.0)) - 0.05;
}

float sdf( in vec3 p )
{
    float cyl1 = sdCappedCylinder( p + vec3(0.0, 0.0, 1.0), 0.5, 0.5 );
    float cyl2 = sdCappedCylinder( p + vec3(0.0, -0.1, 1.0), 0.38, 0.4 );

    float cyl3 = sdCappedCylinder( p + vec3(0.0, -0.1, 1.0), 0.2, 0.4 );
    float water = cyl3 + 0.02*sin(2.0*p.x + 3.0*p.y + 4.0*p.z + iTime*2.0);

    float cup = opSmoothSubtraction(cyl2, cyl1, 0.1);

    float handle = sdTorus( p + vec3(-0.5, 0.0, 1.0), vec2(0.38, 0.08) );
    handle = opSmoothSubtraction(cyl1, handle, 0.08);

    float contents = max(water, cyl2);

    float bgfloor = sdFloor(p);

    return min(min(opSmoothUnion(cup, handle, 0.08), contents), bgfloor);
}

sdfCol sdfWithCol( in vec3 p )
{
    float cyl1 = sdCappedCylinder( p + vec3(0.0, 0.0, 1.0), 0.5, 0.5 );
    float cyl2 = sdCappedCylinder( p + vec3(0.0, -0.1, 1.0), 0.38, 0.4 );

    float cyl3 = sdCappedCylinder( p + vec3(0.0, -0.1, 1.0), 0.2, 0.4 );
    float water = cyl3 + 0.02*sin(2.0*p.x + 3.0*p.y + 4.0*p.z + iTime*2.0);

    float cup = opSmoothSubtraction(cyl2, cyl1, 0.1);

    float handle = sdTorus( p + vec3(-0.5, 0.0, 1.0), vec2(0.38, 0.08) );
    handle = opSmoothSubtraction(cyl1, handle, 0.08);

    // three main components
    float cuphandle = opSmoothUnion(cup, handle, 0.08);
    float contents = max(water, cyl2);
    float bgfloor = sdFloor(p);

    vec4 col = vec4( 0.0, 0.0, 0.0, 1.0 );
    float curMin = 0.0;
    // step 1
    if (cuphandle < contents)
    {
        col = vec4( 1.0, 0.0, 0.0, 1.0 );
        curMin = cuphandle;
    }
    else
    {
        col = vec4( 0.0, 0.0, 1.0, 1.0 );
        curMin = contents;
    }

    return sdfCol( curMin, col );
}

vec3 calcNormal( in vec3 p )
{
    float e = 0.0005;
    float dx = sdf(vec3( p.x + e, p.y, p.z )) - sdf(vec3( p.x - e, p.y, p.z ));
    float dy = sdf(vec3( p.x, p.y + e, p.z )) - sdf(vec3( p.x, p.y - e, p.z ));
    float dz = sdf(vec3( p.x, p.y, p.z + e )) - sdf(vec3( p.x, p.y, p.z - e ));
    return normalize(vec3( dx, dy, dz ));
}

vec3 trace( in vec3 org, in vec3 dir, out bool hit )
{
    hit = false;
    float totalDist = 0.0;
    float dist = 0.0;
    vec3 p = org;
    for ( int step = 0; step < MAXSTEP; step++ )
    {
        dist = sdf( p );
        if ( dist < TOL )
        {
            hit = true;
            break;
        }

        p += dir * dist;
        totalDist += dist;
        if ( totalDist > MAXDIST )
        {
            break;
        }
    }

    return p;
}

vec3 traceWithCol( in vec3 org, in vec3 dir, out bool hit, out vec4 col )
{
    hit = false;
    float totalDist = 0.0;
    sdfCol dist;
    vec3 p = org;
    for ( int step = 0; step < MAXSTEP; step++ )
    {
        sdfCol dist = sdfWithCol( p );
        if ( dist.sdf < TOL )
        {
            hit = true;
            col = dist.col;
            break;
        }

        p += dir * dist.sdf;
        totalDist += dist.sdf;
        if ( totalDist > MAXDIST )
        {
            col = vec4( 1.0, 0.0, 1.0, 1.0 );
            break;
        }
    }

