vec4 TRIANGLE(vec3 color, vec3 background, vec2 v0, vec2 v1, vec2 v2, vec2 uv);

// Rotation matrix around the X axis.
mat2 Rotate2D(float angle)
{
    float cosAngle = cos(angle);
    float sinAngle = sin(angle);

    return mat2(cosAngle, -sinAngle,
                    sinAngle, cosAngle);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // [0; 1] - for the width and height of the screen
    vec2 screen_uv = fragCoord/iResolution.xy;

    // [0; 1] - Color - Red, Green, Blue
    vec3 color = vec3(0.0);

    vec4 VERTEX_BUFFER[4] = vec4[]
    (
        vec4(0.7, 0.75, 1.0, 1.0), //top-right
        vec4(0.3, 0.25, 0.0, 0.0), //bottom-left
        vec4(0.7, 0.25, 0.0, 1.0), //bottom-right
        vec4(0.3, 0.75, 1.0, 0.0)  //top-left

    );

    ivec3 INDEX_BUFFER[2] = ivec3[]
    (
        ivec3(0, 1, 2),
        ivec3(0, 1, 3)
    );

    // Index Buffer Render
    for(int i = 0; i < 2; i += 1) {
        ivec3 idx = INDEX_BUFFER[i];

        float mils = (iDate.w) * 1.0 ;
        vec2 v0 =  Rotate2D(sin(mils) * 0.78539816339) * vec2(VERTEX_BUFFER[idx.x].xy);                
        vec2 v1 =  Rotate2D(sin(mils) * 0.78539816339) * vec2(VERTEX_BUFFER[idx.y].xy);
        vec2 v2 =  Rotate2D(sin(mils) * 0.78539816339) * vec2(VERTEX_BUFFER[idx.z].xy);

        vec2 v0_uv = vec2(VERTEX_BUFFER[idx.x].zw);                
        vec2 v1_uv = vec2(VERTEX_BUFFER[idx.y].zw);
        vec2 v2_uv = vec2(VERTEX_BUFFER[idx.z].zw);

        // Channel X, Y, Z = v0, v1, v2 barycentric weights, 
        // Channel W = 0.0 -> miss || 1.0 -> hit
        vec4 result = TRIANGLE(vec3(0.0), color, v0,  v1,  v2 , screen_uv);

        if(result.w == 1.0) {
            vec2 vertex_uv = v0_uv * result.x + v1_uv * result.y + v2_uv * result.z;

            // Bary Render
            color = result.xyz;

            //UV Render
            //color = vec3(vertex_uv, 0.0);

            //Texture Render
            vertex_uv.x = vertex_uv.x * iChannelResolution[0].x;
            vertex_uv.y = vertex_uv.y * iChannelResolution[0].y;
            //color = texelFetch(iChannel0, ivec2(vertex_uv), 0).xyz;

        }
    }

    fragColor = vec4(color, 1.0);
}

float TRIANGLE_sign(vec2 p1, vec2 p2, vec2 p3)
{
    return (p1.x - p3.x) * (p2.y - p3.y) - (p2.x - p3.x) * (p1.y - p3.y);
}

vec3 barycentrics(vec2 p, vec2 a, vec2 b, vec2 c){
    vec2 v0 = b - a, v1 = c - a, v2 = p - a;
    float den = v0.x * v1.y - v1.x * v0.y;
    float x = (v2.x * v1.y - v1.x * v2.y) / den;
    float z = (v0.x * v2.y - v2.x * v0.y) / den;
    //      
    // x + y + z = 1.0
    float y = 1.0f - x - z; 

    return vec3(x, y, z);
}

vec4 TRIANGLE(vec3 color, vec3 background, vec2 v0, vec2 v1, vec2 v2, vec2 uv)
{
    bool b1 = TRIANGLE_sign(uv, v0, v1) < 0.0f;
    bool b2 = TRIANGLE_sign(uv, v1, v2) < 0.0f;
    bool b3 = TRIANGLE_sign(uv, v2, v0) < 0.0f;

    vec3 bary_res = barycentrics(uv, v0, v1, v2);
    if((b1 == b2) && (b2 == b3)) return vec4(bary_res, 1.0);
    return vec4(background, 0.0);
}

