vec3 dogrid(vec3 color, vec2 w, float px, vec3 c) {
  vec2 uv = rotate(w,0.25*iTime);
  float ldist = length(uv);
  ldist *= 10.0;
  ldist -= round(ldist);
  ldist /= 10.0;
  ldist = abs(ldist);
  float t = atan(uv.y,uv.x);
  t /= PI;
  t *= 20.0;
  t = round(t);
  t /= 20.0;
  t *= PI;
  ldist = min(ldist,line(uv,vec2(0),vec2(cos(t),sin(t))));
  color = mix(c,color,0.75+0.25*smoothstep(0.002,0.004+px,abs(ldist)));
  return color;
}

float R = 1.2;

vec3 docircle(vec3 color, vec2 w, float px, vec3 c) {
  float ldist = length(w)-R;
  color = mix(c,color,smoothstep(0.0,px,abs(ldist)-0.01));
  return color;
}

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  float scale = 1.5;//exp(iTime);
  if (key(CHAR_Z)) scale *= 2.0;
  vec2 z = (2.0*fragCoord - iResolution.xy)/iResolution.y;
  vec2 ci = vec2(0,1);
  z *= scale;
  vec2 t = cpow(cmul(z,z)-vec2(4,0),0.5);
  vec2 w1 = z, w2 = z;
  if (!key(CHAR_X)) {
    w1 = 0.5*(z-t); // Inverse Joukowsky
    w2 = 0.5*(z+t); // The other branch
  }
  vec2 mousepos = 0.4*vec2(cos(0.5*iTime),sin(0.49*iTime)); // Circle location
  if (iMouse.x > 0.0) mousepos = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
  w1 -= mousepos; w2 -= mousepos;
  vec3 color = vec3(0);
  if (!key(CHAR_G)) {
    float mdist = min(abs(z.x),abs(z.y));
    color = mix(vec3(1),color,0.75+0.25*smoothstep(0.01,0.02,mdist));
    float pdist = distance(abs(z),vec2(round(abs(z.x)),0));
    color = mix(vec3(1),color,0.5+0.5*smoothstep(0.04,0.05,pdist));
  }
  if (!key(CHAR_C)) color = docircle(color,z-mousepos,fwidth(z.x),vec3(0.4));
  float px1 = fwidth(w1.x);
  float px2 = fwidth(w2.x);
  color = dogrid(color,w1,px1,vec3(0,1,0));
  color = dogrid(color,w2,px2,vec3(0,1,1));
  color = docircle(color,w1,px1,vec3(1,1,0));
  color = docircle(color,w2,px2,vec3(1,0,0));
  color = pow(color, vec3(0.4545));
  if (alert) color.g = 1.0;
  fragColor = vec4(color,1);
}

