vec2 cmul(vec2 z, vec2 w) {
  return vec2 (z.x*w.x-z.y*w.y, z.x*w.y+z.y*w.x);
}

vec2 cinv(vec2 z) {
  float t = dot(z,z);
  return vec2(z.x,-z.y)/t;
}

vec2 cdiv(vec2 z, vec2 w) {
  return cmul(z,cinv(w));
}

vec2 clog(vec2 z) {
  float r2 = dot(z,z);
  return vec2(0.5*log(r2),atan(z.y,z.x));
}

vec2 clog(float x) {
  return clog(vec2(x,0));
}

vec2 cexp(vec2 z) {
  // If cos and sin were perfect we wouldn't need normalize
  return exp(z.x)*normalize(vec2(cos(z.y),sin(z.y)));
}

vec2 cexp(float k, vec2 z) {
