// loopless + golfing 533 "Sphere fake 3d" by SnackEater. https://shadertoy.com/view/dljfWW
// 272 without Jacobians -  288 with approx J  - 308 with exact J
// --- Unlooping reasoning: -------------------------------------------
//     usually it's easy (e.g. for 2D patterns) : one just has to solve which loop counter is closest:
//     with a term like length(U-P), we must have P ~ U + P constraints (grid sampled, interval, etc).
//     Here it's a bit involved:
//     dot  U ~ P.xy  reverse rot on U  reverse P to a,b
//     trick is to complete U with U.z = sqrt(r - [U|)

/**/

#define r  mat2(cos( iTime +vec4(0,33,11,0)))

void mainImage( out vec4 O, vec2 U )
{   
    vec3  R = iResolution, P;
          U = ( U+U - R.xy ) / R.y;                                // normalize coordinates
    float s = .04365,                                              // s+s = 2Pi/72 = 5 steps
          z = 1. - dot(U,U);
    O *= 0.;             
    z > 0.                                                         // if on sphere
      ? P = vec3( U, sqrt(z) ),                                    // 3D point on front half-sphere
        P.yz *= r,
        P.xz *= r,                                                 // rotate on sphere
        U =  vec2( atan(length(P.xy),P.z), atan(P.x,P.y) ),        // get parameterization
        O.x = 1. - length( ( mod( U + s , s+s ) -s )               // draw blob at grid centers
                       //  / fwidth(U) ) * 140./R.y                // compensate distorsion with Jacobian (approx)
                           * inverse(transpose(mat2(dFdx(U),dFdy(U)))) )  * 1e2/R.y  // ( precise )
      : s;
}




/**  // 289 chars  -5 by timedstamp , -25 by Fab

#define r *= mat2(cos( iTime +vec4(0,33,11,0)))

void mainImage( out vec4 O, vec2 U )
{   
    vec3  R = iResolution, P;
    float z = 1. - dot( U = ( U+U - R.xy ) / R.y , U );
    O *= 0.;             
    z > 0.
      ? P = vec3( U, sqrt(z) ),
        P.yz r,
        P.xz r, 
        O.x = 1. - length(  inverse(mat2(dFdx(U),dFdy(U)))
                           *( fract( U = atan( vec2(length(P.xy),P), P.zy )*11.45 + .5 ) -.5 )
                         ) *1e2/R.y 
      : z;
}

/**/

#define PI     3.1415926535
#define TWO_PI 6.2831853071

#define HEMISPHERE   1      // Disabling this generates uniform random points on a unit sphere. 
#define MAX_POINTS   1024.0 // Max number of generated points.
#define COLOR_POINTS 1      // Enable/Disable random coloring for each point. 

// From quality hashes collection by nimitz: https://www.shadertoy.com/view/Xt3cDn
uint BaseHash(uvec2 p) 
{
    p = 1103515245U * ((p >> 1U) ^ (p.yx));
    uint h32 = 1103515245U * ((p.x) ^ (p.y >> 3U));
    return h32 ^ (h32 >> 16);
}

vec3 Hash3(inout float seed) 
{
    uint n = BaseHash(floatBitsToUint(vec2(seed += 0.1, seed += 0.1)));
    uvec3 rz = uvec3(n, n * 16807U, n * 48271U);
    return vec3(rz & uvec3(0x7fffffffU)) / float(0x7fffffff);
}

vec3 RandomPointInUnitSphere(inout float seed, inout vec3 hash, in float rotationOffset) 
{
    hash = Hash3(seed);
#if HEMISPHERE
    vec3 s = hash * vec3(TWO_PI, 1.0, 1.0) - vec3(rotationOffset, 0.0, 0.0);
#else
    vec3 s = hash * vec3(TWO_PI, 2.0, 1.0) - vec3(rotationOffset, 1.0, 0.0);
#endif
    float theta = s.x;
    float sinPhi = sqrt(1.0 - s.y * s.y); // s.y --> cos(phi)
    float r = pow(s.z, 0.3333333334); // Cuberoot to evenly spread out the points from the center along the radius. 

    return r * vec3(cos(theta) * sinPhi, s.y, sin(theta) * sinPhi);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
#if HEMISPHERE
    uv.y += 0.2;
#endif
    uv *= 3.0;

    vec4 m = iMouse / iResolution.y;

    float currentTime = floor(iTime * MAX_POINTS * 0.1);
    float timeThreshold = MAX_POINTS * 1.3;
    float rOffset = iTime * 0.1;

    vec3 outColor = vec3(0.0);

    float numPoints = min(MAX_POINTS, mod(currentTime, timeThreshold));
    float hashSeed = floor(currentTime / timeThreshold);

    if (m.z > 0.0)
    {
        rOffset = -m.x * 4.0;
    }

    vec3 point;
    vec2 pointUv;
    vec3 hash;
    for (float i = 0.0; i < numPoints; ++i)
    {
        point = RandomPointInUnitSphere(hashSeed, hash, rOffset);
        pointUv = uv * (point.z * 0.25 + 0.75);

        outColor += 4e-5 / pow(length(pointUv - point.xy), 1.6) * ((1.0 - point.z) * 1.6 + 0.8) 
#if COLOR_POINTS
        * hash.zyx * 0.32
#else
        * 0.16
#endif
        ;
    }

    float t = fragCoord.x + fragCoord.y * iTime;
    fragColor = vec4(pow(outColor, vec3(1.0 / 2.2)) - Hash3(t).xxx * 0.04, 1.0);
}

#define HUGE 1000000.0
#define PI 3.14159265359

bool isNotePlaying(int note)
{
    //return false;
    return texelFetch(iChannel1, ivec2(note, 0), 0).r != 0.;
}

// computes the ray direction of a screen pixel
vec3 perspectiveRay(vec2 pixel)
{
      float x = -1.0 + 2.0*(pixel.x / iResolution.x);
      float z = -1.0 + 2.0*(pixel.y / iResolution.y);
      z *= iResolution.y / iResolution.x;  // aspect ratio
      float y = 1.0;
      return normalize(vec3(x,y,z));
}

// rotation about the z axis
vec3 rotateYaw(vec3 v, float angle)
{
    float s = sin(angle);
    float c = cos(angle);
    return vec3(v.x * c - v.y * s, v.y * c + v.x * s, v.z);
}

// rotation about the x axis
vec3 rotatePitch(vec3 v, float angle)
{
    float s = sin(angle);
    float c = cos(angle);
    return vec3(v.x, v.y * c - v.z * s, v.z * c + v.y * s);
}

// rotation about the y axis
vec3 rotateRoll(vec3 v, float angle)
{
    float s = sin(angle);
    float c = cos(angle);
    return vec3(v.x * c - v.y * s, v.y, v.z * c + v.x * s);
}

vec3 lightDir = normalize(vec3(1,1,-1.5));

float calcLight(vec3 normal, vec3 rd, float shininess)
{
    float ambient  = 0.5;
    float diffuse  = max(dot(-lightDir,normal), 0.0);
    float specular = max(dot(-lightDir,reflect(rd, normal)), 0.0);
    specular *= specular*specular*specular*specular*specular*specular*specular*specular;
    return ambient + diffuse + specular * shininess;
}

// subtracts distance b from a
float sdfSubtract(float a, float b)
{
    return max(a, -b);
}

// based on Inigo Quilez's SDF functions
float sdfBox(vec3 p, vec3 center, vec3 b)
{
    vec3 q = abs(p-center) - b;
    return length(max(q,0.0)) + min(max(max(q.x,q.y),q.z),0.0);
}

// like sdfBox, but specifying minimum and maximum bounds
float sdfBoxBounds(vec3 p, vec3 bmin, vec3 bmax)
{
    return sdfBox(p, .5*(bmin+bmax), .5*(bmax-bmin));
}

int whiteKeyToNote(float key)
{
    int octave = int(floor(key / 7.));
    key = mod(key, 7.);
    int note = int(key) * 2;
    if (key >= 3.)
        note--;
    return octave * 12 + note + 39;
}

int blackKeyToNote(float key)
{
    int octave = int(floor(key / 7.));
    key = mod(key, 7.);
    int note = int(key) * 2 + 1;
    if (key >= 2.)
        note--;
    return octave * 12 + note + 39;
}

float sdfBlackKey(vec3 p, float key)
{
    key = clamp(key, -23., 26.);
    // if this is the spot between B-C or E-F with no black key, choose the closest black key instead
    if (mod(key,7.) == 2. || mod(key,7.) == 6.)
        key += fract(p.x) >= .5 ? 1. : -1.;
    int note = blackKeyToNote(key);
    float z = isNotePlaying(note) ? .1 : .5;
    return sdfBox(p, vec3(key+.5,1.,z), vec3(.25, 1., .5));
}

float sdfWhiteKey(vec3 p, float key)
{
    key = clamp(key, -23., 28.);
    int note = whiteKeyToNote(key);
    float z = isNotePlaying(note) ? -.5 : 0.;
    return sdfBox(p, vec3(key,0.,z), vec3(.45, 2., .5))-.05;
}

float sdfWhiteKeys(vec3 p)
{
    float key = round(p.x);  // get nearest key
    //return sdfWhiteKey(p, key);
    // If key is pressed, we need to SDF the neighboring keys as well.
    float a = sdfWhiteKey(p, key - 1.);
    float b = sdfWhiteKey(p, key);
    float c = sdfWhiteKey(p, key + 1.);
    return min(min(a, b), c);
}

float sdfBlackKeys(vec3 p)
{
    float key = round(p.x-.5);  // get nearest key
    return sdfBlackKey(p, key);
}

float globalSdf(vec3 p, out vec3 color)
{
    vec3 bmin = vec3(-25.5, -2.5, -4);
    vec3 bmax = vec3(30, 30, 4);
    color = vec3(.1,.1,.1);
    float d;
    float dCutout;

    d = sdfBoxBounds(p, bmin, bmax);
    // middle part
    d = min(d, sdfBoxBounds(p, bmin + vec3(1, 10, -20), bmax + vec3(-1, -1, 15)));
    // back of piano body
    d = min(d, sdfBoxBounds(p, bmin + vec3(0, 9, 8+13), bmax + vec3(0, 0, 15)));
    // left leg
    d = min(d, sdfBox(p, vec3(bmin.x+3., bmin.y+3., -4 - 10), vec3(1, 1, 10)));
    // right leg
    d = min(d, sdfBox(p, vec3(bmax.x-3., bmin.y-3., -4 - 10), vec3(1, 1, 10)));
    // cut out keyboard area
    bmin.y--;bmax.z++;
    bmin.x += 2.;
    bmax.x -= 2.;
    bmax.y = 2.;
    bmin.z = -.5;
    dCutout = sdfBox(p, .5*(bmin+bmax), .5*(bmax-bmin));
    d = sdfSubtract(d, dCutout);

    if (dCutout < 0.)  // optimization: only SDF the keys if we're in the cutout
    {
        float dBlack = sdfBlackKeys(p);
        d = min(d, dBlack);
        float dWhite = sdfWhiteKeys(p);
        if (dWhite <= d)
            color = vec3(.8, .8, .8);
        d = min(d, dWhite);
    }
    return d;
}

vec3 calcNormal(vec3 p, float d)
{
    float e = 0.0001;
    vec3 dummy;
    vec3 n = vec3(
        globalSdf(p + vec3(e,0,0), dummy),
        globalSdf(p + vec3(0,e,0), dummy),
        globalSdf(p + vec3(0,0,e), dummy));
    return normalize(n - d);
}

// basic environment mapping with a cubemap texture
vec4 env(vec3 rd)
{
    return texture(iChannel0, rd.xzy * vec3(1,1,-1));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 ro;  // ray origin
    vec3 rd;  // ray direction
    float yaw = PI/8.;
    float pitch = -PI/8.;

    ro = vec3(18, -18, 15);

    rd = perspectiveRay(fragCoord);
    rd = rotateYaw(rotatePitch(rd, pitch), yaw);

    fragColor = env(rd);

    // ray march the piano
    float t = 0.;
    for (int i = 0; i < 30; i++)
    {
        vec3 p = ro + rd * t;
        vec4 color;
        float d = globalSdf(p, color.rgb);
        if (d < 0.05)
        {
            vec3 normal = calcNormal(p, d);
            vec3 refl = reflect(rd, normal);
            color = mix(color, env(refl), 0.25);
            fragColor.rgb = color.rgb * calcLight(normal, rd, 0.5);
            break;
        }
        t += d;
        if (t > 100.)
            break;
    }
    //fragColor = texture(iChannel1, vec2(fragCoord.x / iResolution.x, 0));

    // fade out
    float time = iTime;
    if (time >= 177.0)
        fragColor.rgb = mix(fragColor.rgb, vec3(0), (time - 177.0) / 3.);
}

