vec2 points[4] = vec2[4](vec2(.5,.05), vec2(.5,.33), vec2(.5,.66), vec2(.5,.95));


float scalarSin(float time){
    return (sin(time)+1.0)*0.5;
}

//https://gist.github.com/sketchpunk/778c8aa561ca62142beecf849ad2cd57
vec3 vecQuatRotation(vec4 q, vec3 v){ 
  return v + cross(2.0 * q.xyz, cross(q.xyz, v) + q.w * v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec3 uv = vec3(vec2(fragCoord/iResolution.y),0.0);

    float minDist = 1.0;
    for(int i = 0; i < points.length(); i++){
        points[i].x = scalarSin(iTime * (1.0/float(i+1)));
        float dist = distance(points[i],uv.xy);
        minDist = min(dist,minDist);
    }

    minDist = pow(1.0-minDist,3.0);

    #ifdef IS_CINESHADER
    uv.z = minDist;

    //Calculate Normal from height
    float scale = 1.0;
    float Nx = (minDist * (uv.x + 1.0, uv.y) - minDist * (uv.x - 1.0, uv.y)) / (2.0 * scale);
    float Ny = (minDist * (uv.x, uv.y + 1.0) - minDist * (uv.x, uv.y - 1.0)) / (2.0 * scale);
    float Nz = 1.0 / sqrt(1.0 + pow(Nx,2.0) + pow(Ny,2.0));

    vec3 normal = vec3(Nx, Ny, Nz);

    vec3 lightPos = vec3(cos(iTime),0.0,1.0);

    // Calculate the to center distance
    float d = length(uv - 0.5) * 2.0;
    // Calculate time
    float t = d * d * 25.0 - iTime * 3.0;
    // Time varying surface color
    vec3 surfaceColour = 0.5 + 0.5 * cos(t / 20.0 + uv.xyx + vec3(0.0,2.0,4.0));
    vec3 lightColour = vec3(1.0);
    vec3 specularStrength = vec3(1.0);
    float shininess = 512.0;

    vec3 lambertian = max(dot(lightPos, normal), 0.0)*surfaceColour*lightColour;

    // Calculate the view vector
    vec3 viewVector = vecQuatRotation(iCamQuat,iCamPos);

    vec3 viewDir = normalize(viewVector - uv);
    vec3 lightDir = normalize(lightPos - uv);
    vec3 halfDir = normalize(viewDir + lightDir);

    float specular = pow(max(dot(normal, halfDir), 0.0), shininess);
    vec3 specularColour = specularStrength * specular * lightColour;

    // Output to screen
    fragColor = vec4(lambertian+specularColour,minDist);
    #else
    fragColor = vec4(minDist,minDist,minDist,1.0);
    #endif
}

