vec4 T (vec2 U) {
    if (iFrame % 4 == 0) {
        U -= .5*v(A(U).xy);
        U -= .5*v(A(U).xy);
        U -= .5*v(A(U).xy);
        U -= .5*v(A(U).xy);
    }
    return A(U);
}
Main {

    Q = T(U);
    vec4 n = T(U+vec2(0,1));
    vec4 e = T(U+vec2(1,0));
    vec4 s = T(U-vec2(0,1));
    vec4 w = T(U-vec2(1,0));

    Q.xy -= .5*vec2(e.z-w.z,n.z-s.z);


    if (iMouse.z>0.&&length(U-iMouse.xy) < 10.) Q.y = .6;

    if (iFrame < 1) {
        vec2 v = normalize(U-.5*R);
        Q.xy = vec2(v.y,-v.x)*.1+v*sin(6.*atan(U.y-.5*R.y,U.x-.5*R.x));

        Q.xy *= 2.*exp(-10.*length(U-.5*R)/R.y);
    }

    if (U.x < 2.||R.x-U.x<2.||U.y < 2.||R.y-U.y<2.) Q.xy *= 0., Q.z *= .9;

}

