    PLAYNOTES(Fs4, FREQ_Fs(4))

    return a;
}

vec2 mainSound( int samp, float time )
{
    time = timeTrack(time);

    vec2 sound = vec2(0);

    float right = channel1(time) * 0.25;
    float left = channel2(time) * 0.25;

    // mix the channels
    sound += left * vec2(0.7, 0.3);
    sound += right * vec2(0.3, 0.7);

    return sound;
}

