#define H iResolution.y
#define point vec2
#define pair vec4
#define line vec3
#define circle vec3
#define EPSILON 0.0001
#define INFINITY 1e7
#define AA( uv ) ( 0.5 * ( dFdx( uv.x ) + dFdy( uv.y ) ) )

#define CYAN vec4( 0, 1, 1, 1 )
#define MAGENTA vec4( 1, 0, 1, 1 )
#define YELLOW vec4( 1, 1, 0, 1 )

// line that joins two points
line join( point P1, point P2 )
{
	return vec3( P1.y - P2.y, P2.x - P1.x, P1.x * P2.y - P1.y * P2.x );
}

// point that meets two lines
point meet( line L1, line L2 ) {
    return point( L1.y * L2.z - L1.z * L2.y, L1.z * L2.x - L1.x * L2.z ) / ( L1.x * L2.y - L1.y * L2.x );
}

// perpendicular bisector between two points
line bisect( point P1, point P2 ) {
    return line( 2. * ( P1 - P2 ), dot( P2, P2 ) - dot( P1, P1 ) );
}

// circle that links three points
circle link(in point a, in point b, in point c) {
    point t = b - a;
    b = c - a;
    float d = 2. * (t.x * b.y - t.y * b.x);
    c = t * dot(b, b);
    b *= dot(t, t);
    t = point(b.y - c.y,c.x - b.x) / d;
    return circle(t + a, length(t));
}

// "distance" between line and point
float dist( line L, point P )
{
	return ( P.x * L.x + P.y * L.y );
}

// "distance" between point and line
float dist( point P, line L )
{
	return ( dist( L, P ) + L.z ) / ( L.x * L.x + L.y * L.y );
}

// project point to line
point project( point P, line L )
{
	return P - ( dist( P, L ) * L.xy );
}

// project line to point
line project( line L, point P )
{
	return line( L.xy, -( P.x * L.x + P.y * L.y ) );
}

// reflect point across line (2x projection)
point mirror( point P, line L )
{
	return P - ( 2. * dist( P, L ) * L.xy );
}

// reflect line across point (2x projection)
line mirror(line L, point P) {
    return line(L.xy, L.z - 2. * ( ( L.x * L.x + L.y * L.y ) * dist( P, L )));
}

// pair of points tangent of a point and circle
// thanks mla, elenzil, jeyko: https://www.shadertoy.com/view/DtBBWc
// simplified by me
pair tangents(point P, circle C)
{
    P -= C.xy;
    float d = dot( P, P ) / ( C.z * C.z );
    P = P / d;
    point t = ( vec2( -P.y, P.x ) * sqrt( d - 1. ) );
    return pair( P + C.xy + t, P + C.xy - t );
}

// line from the tangents of a point and circle
line segment(point P, circle C)
{
    pair t = tangents( P, C );
    return join( t.xy, t.zw );
}

// pair of points that intersect a circle
pair intersect(line L, circle C)
{
    point p = project(C.xy,L);
    float d = distance(C.xy,p);
    vec2 n = normalize(vec2(-L.y, L.x)) * sqrt(C.z * C.z - d * d);
    return pair(p - n, p + n);
}

