{
    vec2 uv = fragCoord / iResolution.xy;
    //uv.x *= iResolution.x / iResolution.y;

    float size = 8.0;

    //vec2 Pos = size*uv;
    vec2 Pos = floor(size*uv);     // floor effectively does steps
