    // Background layer
	vec4 layer1 = vec4(rgb(210.0, 222.0, 228.0), 1.0);

    vec2 uv2 = uv-center;
    float angle = atan(uv2.y, uv2.x);
    radius += sin(angle * 20.0) * 50.0 * pow(sin(iTime * 0.5), 6.0);

