vec2 rayLineIntersect(
    vec2 ro,
    vec2 rd,
    vec2 A,
    vec2 B,
    out vec2 norm
){
    // dot(N, ro + rd * k - A) = 0
    // dot(N, ro - A) + dot(N,rd*k) = 0
    // dot(N,rd*k) = -dot(N, ro - A)
    // k * dot(N,rd) = -dot(N, ro - A)
    // k = -dot(N, ro - A) / dot(N,rd)


    vec2 Tan = (B - A);
    vec2 N = vec2(Tan.y,-Tan.x);


    float k = -dot(N, ro - A)/dot(N,rd);

    if(k < 0.){
        return vec2(0);
    }

    vec2 p = ro + rd * k;

    float proj = dot(Tan, p - A);

    if(proj < 0. || proj > dot(Tan,Tan)){
        return vec2(0);
    }

    norm = -N;
    if(dot(-rd, norm) < 0.0){
        norm = -norm;
    }

    return ro + rd * k;
}


#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))
#define pi acos(-1.)

// From iq
float sdSegment( in vec2 p, in vec2 a, in vec2 b ){
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 C, in vec2 U ){
    vec2 uv = (U - 0.5*iResolution.xy)/iResolution.y;
    vec2 muv = (iMouse.xy - 0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec2 ro = -vec2(0.5,0.);

    vec2 rd = normalize(vec2(muv - ro));

    if(iFrame == 0){
        //ro = vec2(0.4);
        rd = normalize(-ro);
    }

    vec2 A = vec2(sin(iTime),cos(iTime))*0.2;
    vec2 B = vec2(sin(iTime + 3.14),cos(iTime + 3.14))*0.2;

    vec2 norm;

    vec2 c = rayLineIntersect(
        ro, rd, A, B, norm
    );
    float sd = 1000.0;

    sd = min(sd, length(ro - uv) - 0.02);
    sd = min(sd, length(c - uv) - 0.02);
    sd = min(sd, sdSegment(uv,A,B) - 0.002);
    sd = min(sd, sdSegment(uv,ro,c) - 0.002);

    sd = min(sd, sdSegment(uv,c,c + norm*0.3) - 0.002);



    col += smoothstep(fwidth(uv.y),0.,sd);

    col = 1.-col;
    col = pow(col,vec3(0.454545));
    C = vec4(col,1.0);
}

#define PI 3.141592653

float distToSegment( vec2 ps1, vec2 ps2, vec2 point)
{
    vec2 pa = point - ps1;
    vec2 ba = ps2 - ps1;

    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0);

    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixel = fragCoord.xy;
    vec2 midpoint = iResolution.xy / 2.0;
    vec3 col = vec3(0);
    const vec3 green = vec3(0.0, 1.0, 0.0);
    const vec3 blue = vec3(0.35,0.76,0.83);
    float lineThickness = 1.0;
    float rayLen = sqrt(pow(iResolution.x, 2.0) + pow(iResolution.y, 2.0));
    float radius = min(iResolution.x, iResolution.y) * 0.45;

    const float period = 5.0;
    float angle = 0.0;
    float rotPercent = mod(-iTime, period) / period;
    angle = 2.0 * PI * rotPercent;
    vec2 rayDir = vec2(cos(angle), sin(angle));
    vec2 rayPerp = vec2(-sin(angle), cos(angle));
    vec2 pixelDir = normalize(pixel - midpoint);

    //Trail
    if(dot(rayDir, pixelDir) > 0.0 && dot(pixelDir, rayPerp) > 0.0)
    {
        col = green * pow(dot(rayDir, pixelDir), 1.0) * 0.4;

    }

    //Ray
    float distToRay = distToSegment(midpoint, midpoint + rayDir * rayLen, pixel);
    if(distToRay <= lineThickness)
    {
        col = green * (lineThickness / distToRay) ;//- 3.0 * ( distance(pixel, midpoint) / radius);
    }

    //Circle
    if(true)
    {
        float distToCircle = abs(distance(midpoint, pixel) - radius);
        if(distToCircle <= lineThickness * 2.0)
        {
            col += green * (1.0 - (distToCircle / (lineThickness * 2.0)));
        }

        if(distance(midpoint, pixel) > radius + 2.)
        {
            col = vec3(0.0);
        }
    }

    fragColor = vec4(col, 1.0);
}

