void mainImage (out vec4 ImgOut, vec2 f) {
  vec2 R = iResolution.xy;
  ivec2 I = ivec2(f);
  ivec2 N = ivec2(R);
  int rs = N.y/nrows;
  int rb = I.y/rs*rs;
  int i = nrows-1-I.y/rs;
  vec2 u = f/R;

  vec3 O = vec3(0.);

  int my = int(iMouse.y);
  int methodsel=nrows-1-my/rs;
  if(iMouse.y==0.)methodsel=8;
  int rbm = (nrows-1-methodsel)*rs;

  if(u.x<.5) {
    O = texture(iChannel0,u*vec2(2,1)).rrr;
    //if(I.y==rbm+rs/2) O.g=1.;
    ImgOut.rgb = O;
    /* re-render the visual reference gradient at high quality using the best dither option */ if(i==9) for(int c=0;c<2;c++) ImgOut[c]=display_dither(8,hash(I.x+N.x*I.y)^iFrame,64,EOTF(O[c]));
  } else {
    u.x=u.x*2.-1.;
    vec4 d = texelFetch(iChannel1,ivec2(u.x*R.x,rbm),0);
    float y,t,s=exp2(3.-float(nbits));

    if(u.x<.03&&abs(float(rbm+rs/2-I.y)/R.y)<.5*u.x) O.g=1.;

    bool isadj = (methodsel>=5&&methodsel<=7);
    bool isprop = (iMouse.x/R.x>.8);

    O+=description(methodsel,isadj,isprop,u);

    if(isprop) d=.5*d/d.w;

    y=u.y; t=1./R.y;
    // grey plot: exact value
    if (t>abs(-y+d.w)) O+=.1;
    // blue plot: average result
    if (t>abs(-y+d.x)) O.b++;

    y=(u.y*2.-1.)*.2*s; t=.4/R.y*s;
    // grey plot: zero line
    if (t>abs(-y)) O+=.1;
    // red plot: error
    if (t>abs(-y+d.x-d.w)) O.r++;

    y=u.y*.1*s*s; t=.1/R.y*s*s;
    // green plot: variance (noise)
    if (t>abs(-y+d.y)) O.g++;
    ImgOut.rgb = IEOTF(O);
  }
}

