vec3 description (int method, bool isadj, bool isprop, vec2 u) {
#define tex iChannel2
  u.y=1.-u.y;
  u*=vec2(36,20);
  u.x-=2.;
  float x=u.x;
  vec3 v=vec3(0);
  if(method<9) {
    u.x=x;
    v+= draw_string(tex,u, str_method), u.x-=6.+1.;
    v+= draw_string(tex,u,uint[2](1u,uint(0x30+method)));
    u.y--;
  }
  u.x=x;
  if(method==0) v+= draw_string(tex,u,str_method0);
  if(method==1) v+= draw_string(tex,u,str_method1);
  if(method==2) v+= draw_string(tex,u,str_method2);
  if(method==3) v+= draw_string(tex,u,str_method3);
  if(method==4) v+= draw_string(tex,u,str_method4);
  if(method==5) v+= draw_string(tex,u,str_method5);
  if(method==6) v+= draw_string(tex,u,str_method6);
  if(method==7) v+= draw_string(tex,u,str_method7);
  if(method==8) v+= draw_string(tex,u,str_method8);
  if(method==9) v+= draw_string(tex,u,str_method9);
  v*=.2;
  u.y--;
  v+= vec3(0,0,1)*draw_string(tex,u,str_key0), u.x-=7.+2.;
  v+= vec3(1,0,0)*draw_string(tex,u,str_key1), u.x-=5.+2.;
  v+= vec3(0,1,0)*draw_string(tex,u,str_key2), u.x-=8.+2.;
  u.y--;
  u.x=x;
  if(isadj) v+= vec3(.2)*draw_string(tex,u,str_adjusted), u.x-=8.+1.;
  if(isprop) v+= vec3(.2)*draw_string(tex,u,str_scaleprop);
  else       v+= vec3(.2)*draw_string(tex,u,str_scaleabs);
  return v;
}

