// config tests

const int nbits = 4;

#define EOTF srgb_eotf
#define IEOTF srgb_ieotf

//#define EOTF gamma22_eotf
//#define IEOTF gamma22_ieotf



#define T vec4

T srgb_ieotf (T c) {
  return mix(12.92*c,1.055*pow(c,T(1./2.4))-.055,step(.0031308,c));
}

T srgb_eotf (T e) {
  return mix(e/12.92,pow((e+.055)/1.055,T(2.4)),step(.04045,e));
}

T gamma22_ieotf (T c) {
  return pow(c,T(1./2.2));
}

T gamma22_eotf (T e) {
  return pow(e,T(2.2));
}

// generics
#define OVERLOAD_VECN(N) \
  float N (float v) { return N(vec4(v)).x; } \
  vec2 N (vec2 v) { return N(v.xyxx).xy; } \
  vec3 N (vec3 v) { return N(v.xyzx).xyz; }
OVERLOAD_VECN(srgb_ieotf)
OVERLOAD_VECN(srgb_eotf)
OVERLOAD_VECN(gamma22_ieotf)
OVERLOAD_VECN(gamma22_eotf)

#undef T

uint hash_triple32(uint x) {
  // https://nullprogram.com/blog/2018/07/31/
  x ^= x >> 17;
  x *= 0xED5AD4BBu;
  x ^= x >> 11;
  x *= 0xAC4C1B51u;
  x ^= x >> 15;
  x *= 0x31848BABu;
  x ^= x >> 14;
  return x;
}
#define hash hash_triple32
int hash(int x) { return int(hash(uint(x))); }


float probmix(float A, float B, float C, float var, float rnd) {
  float PB = clamp((A*C+var)/((B-A)*(B-C)),0.,.75);
  float PA = mix(C,B,PB)/(C-A);
  /* float PC = 1.-PA-PB; */

  // sanity check probabilities
  // some fail, but with insignificant consequence
  //if(PA<0.||PC<0.)return 10.;
  //if(PA>1.||PC>1.)return 10.;

  if(rnd<PA)
    return A;
  if(rnd<PA+PB)
    return B;
  /* if(rnd<PA+PB+PC) */ // cum prob = 1
    return C;
}

const int nrows = 10;

float display_dither (int method, int seed, int nlevels, float x) {

  float qres = 1./float(nlevels-1);
  vec2 rnd = .5*unpackSnorm2x16(hash(uint(seed)));

  // noise with rectangular probability density function
  float rpdf = rnd.x;

  // noise with triangular probability density function
  float tpdf = rnd.x+rnd.y;

  float r;
  if (method==0) {
    // no dither
    return IEOTF(x);
  }
  if (method==1) {
    // bad: RPDF dither before IEOTF
    float xd = x + rpdf*.05;
    return IEOTF(xd);
  }
  if (method==2) {
    // bad: TPDF dither before IEOTF
    float xd = x + tpdf*.05;
    return IEOTF(xd);
  }
  if (method==3) {
    // bad: RPDF dither in the space that will be quantized
    float y = IEOTF(x);
    y += rpdf*qres;
    return y;
  }
  if (method==4) {
    // bad: TPDF dither in the space that will be quantized
    //      with no avoidance of clipping
    float y = IEOTF(x);
    y += tpdf*qres;
    return y;
  }
  if (method==5) {
    // good: TPDF dither in the space that will be quantized
    // Note that with 8 bit depth, errors are imperceptible.
    // Any further refinement yields especially high accuracy, or
    // else improvements noticeable only at lower bit depths.
    // 6 bits is common for low cost LCDs.
    x *= (1.-qres);
    float y = IEOTF(x);
    y += tpdf*qres;
    return y;
  }
  if (method==6) {
    // better: quantized space TPDF dither, hacky linear quantization
    x *= (1.-qres);
    float y = IEOTF(x);
    y += tpdf*qres;
    float yl = floor(y/qres)*qres;
    float yh = yl+qres;  /* ceil(y/bitres)*bitres */
    y = 2.*EOTF(y) > EOTF(yl)+EOTF(yh) ? yh : yl;
    return y;
  }
  if (method==7) {
    // bad: quantized space RPDF dither, optically linear quantization
    x *= (1.-qres);
    float y = IEOTF(x);
    // lower possible value
    float yc = floor(y/qres)*qres;
    // delta to upper possible value
    float yd = qres;
    // what fraction is optical intensity between these values
    float f = (x-EOTF(yc))/(EOTF(yc+yd)-EOTF(yc));
    // select value depending on random threshold
    y = f-.5>rpdf ? yc+yd : yc;
    return y;
  }
  if (method==8) {
     // best: probabilistic mix of 3 quantized values for carefully controlled variance
    float y = IEOTF(x);
    float yb = round(y/qres)*qres;
    float A = EOTF(clamp(yb-qres,0.,1.));
    float B = EOTF(yb);
    float C = EOTF(clamp(yb+qres,0.,1.));
    float d = (EOTF(y+.01*qres)-x)/.01; // derivative
    float var = d*d*.25;
    //float var = sqrt(x)*.0001;
    float xd = x+probmix(A-x,B-x,C-x,var,rnd.x+.5);
    y = IEOTF(xd);
    y = round(y/qres)*qres;
    return y;
  }
}




