void mainImage (out vec4 O, vec2 f) {
  vec2 R = iResolution.xy;
  ivec2 I = ivec2(f);
  ivec2 N = ivec2(R);
  int rs = N.y/nrows;
  int i = nrows-1-I.y/rs;
  int seed = I.x+N.x*I.y;
  seed = hash(seed)^iFrame;

  float x = f.x/R.x;
  x=x*x*x;

  int nlevels=1<<nbits;
  float bitres = 1./(exp2(float(nbits))-1.);
  float y = display_dither(i,seed,nlevels,x);

  // quantize
  float qres = 1./float(nlevels-1);
  y = round(y/qres)*qres;
  y = clamp(y,0.,1.);

  if (i>=5&&i<=7) {
    x *= (1.-qres);
  }

  if (i==9) { // reference level
    y = IEOTF(x);
  }

  O.x = y;
  O.w = x;
}

