void mainImage (out vec4 O, vec2 f) {
  vec2 R = iResolution.xy;
  ivec2 I = ivec2(f);
  ivec2 N = ivec2(R);
  int rs = N.y/nrows;
  int rb = I.y/rs*rs;
  int i = nrows-1-I.y/rs;

  O = texelFetch(iChannel0,I,0);
  vec4 d = texelFetch(iChannel1,I,0);
  vec4 v = vec4(0.);
  v.rgb = EOTF(d.rgb);

  if(I.y==rb) {
    v = vec4(0.);
    for (int i=0;i<rs;i++) {
      float x = EOTF(texelFetch(iChannel1,ivec2(I.x,rb+i),0).r);
      v.x += x;
      v.y += x*x;
    }
    v /= float(rs);
    v.y -= v.x*v.x;
    v.w = d.w;
  }
  float M=iMouse.x/R.x;
  O = M<.4?v:mix(O,v,M<.95?.01:.001);
}

