// -------------------------------

float wrap(float inp, float start, float end)
{
    return start + mod(inp - start, end - start);
}

float lerp(float a, float b, float t)
{
    return a + t * (b - a);
}

vec3 lerp(vec3 a, vec3 b, float t)
{
    return a + t * (b - a);
}

float map_range(float inp, float inp_start, float inp_end, float out_start, float out_end)
{
    return out_start + ((out_end - out_start) / (inp_end - inp_start)) * (inp - inp_start);
}

float map_range_clamp(float inp, float inp_start, float inp_end, float out_start, float out_end)
{
    float t = clamp((inp - inp_start) / (inp_end - inp_start), 0.0, 1.0);
    float v = out_start + t * (out_end - out_start);
    return v;
}

vec3 blender_rgb_to_hsv(vec3 rgb)
{
    float cmax, cmin, h, s, v, cdelta;
    vec3 c;

    cmax = max(rgb[0], max(rgb[1], rgb[2]));
    cmin = min(rgb[0], min(rgb[1], rgb[2]));
    cdelta = cmax - cmin;

    v = cmax;
    if (cmax != 0.0)
    {
        s = cdelta / cmax;
    }
    else
    {
        s = 0.0;
        h = 0.0;
    }

    if (s == 0.0)
    {
        h = 0.0;
    }
    else
    {
        c = (vec3(cmax) - rgb.xyz) / cdelta;

        if (rgb.x == cmax)
        {
            h = c[2] - c[1];
        }
        else if (rgb.y == cmax)
        {
            h = 2.0 + c[0] - c[2];
        }
        else
        {
            h = 4.0 + c[1] - c[0];
        }

        h /= 6.0;

        if (h < 0.0)
        {
            h += 1.0;
        }
    }

    return vec3(h, s, v);
}

vec3 blender_hsv_to_rgb(vec3 hsv)
{
    float i, f, p, q, t, h, s, v;
    vec3 rgb;

    h = hsv[0];
    s = hsv[1];
    v = hsv[2];

    if (s == 0.0)
    {
        rgb = vec3(v, v, v);
    }
    else
    {
        if (h == 1.0)
        {
            h = 0.0;
        }

        h *= 6.0;
        i = floor(h);
        f = h - i;
        rgb = vec3(f, f, f);
        p = v * (1.0 - s);
        q = v * (1.0 - (s * f));
        t = v * (1.0 - (s * (1.0 - f)));

        if (i == 0.0)
        {
            rgb = vec3(v, t, p);
        }
        else if (i == 1.0)
        {
            rgb = vec3(q, v, p);
        }
        else if (i == 2.0)
        {
            rgb = vec3(p, v, t);
        }
        else if (i == 3.0)
        {
            rgb = vec3(p, q, v);
        }
        else if (i == 4.0)
        {
            rgb = vec3(t, p, v);
        }
        else
        {
            rgb = vec3(v, p, q);
        }
    }

    return rgb;
}

vec3 blender_hue_sat(vec3 col, float hue, float sat, float value)
{
    vec3 hsv = blender_rgb_to_hsv(col);

    hsv[0] = fract(hsv[0] + hue + 0.5);
    hsv[1] = clamp(hsv[1] * sat, 0.0, 1.0);
    hsv[2] = hsv[2] * value;

    return blender_hsv_to_rgb(hsv);
}

float rgb_avg(vec3 inp)
{
    return (inp.x + inp.y + inp.z) / 3.0;
}

float rgb_sum(vec3 inp)
{
    return inp.x + inp.y + inp.z;
}

float rgb_max(vec3 inp)
{
    return max(max(inp.x, inp.y), inp.z);
}

float rgb_min(vec3 inp)
{
    return min(min(inp.x, inp.y), inp.z);
}

vec3 rgb_uniform_offset(vec3 inp, float black_point, float white_point)
{
    float mono = rgb_avg(inp);
    float mono2 = map_range_clamp(mono, black_point / 1000.0, 1.0 - (white_point / 1000.0), 0.0, 1.0);
    return inp * (mono2 / mono);
}

vec3 rgb_sweep(float hue)
{
    hue = wrap(hue * 360.0, 0.0, 360.0);

    vec3 col = vec3(1, 0, 0);
    col = mix(col, vec3(1, 1, 0), map_range_clamp(hue, 0.0, 60.0, 0.0, 1.0));
    col = mix(col, vec3(0, 1, 0), map_range_clamp(hue, 60.0, 120.0, 0.0, 1.0));
    col = mix(col, vec3(0, 1, 1), map_range_clamp(hue, 120.0, 180.0, 0.0, 1.0));
    col = mix(col, vec3(0, 0, 1), map_range_clamp(hue, 180.0, 240.0, 0.0, 1.0));
    col = mix(col, vec3(1, 0, 1), map_range_clamp(hue, 240.0, 300.0, 0.0, 1.0));
    col = mix(col, vec3(1, 0, 0), map_range_clamp(hue, 300.0, 360.0, 0.0, 1.0));

    return col;
}

// -------------------------------

vec3 flim_rgb_sweep(vec2 uv0to1)
{
    float hue = 1.0 - uv0to1.y;
    float exposure = map_range(uv0to1.x, 0.0, 1.0, -5.0, 10.0);
    return rgb_sweep(hue) * pow(2.0, exposure);
}

// https://www.desmos.com/calculator/khkztixyeu
float flim_super_sigmoid(float inp, float toe_x, float toe_y, float shoulder_x, float shoulder_y)
{
    // Clip
    inp = clamp(inp, 0.0, 1.0);
    toe_x = clamp(toe_x, 0.0, 1.0);
    toe_y = clamp(toe_y, 0.0, 1.0);
    shoulder_x = clamp(shoulder_x, 0.0, 1.0);
    shoulder_y = clamp(shoulder_y, 0.0, 1.0);

    // Calculate straight line slope
    float slope = (shoulder_y - toe_y) / (shoulder_x - toe_x);

    // Toe
    if (inp < toe_x)
    {
        float toe_pow = slope * toe_x / toe_y;
        return toe_y * pow(inp / toe_x, toe_pow);
    }

    // Straight line
    if (inp < shoulder_x)
    {
        float intercept = toe_y - (slope * toe_x);
        return slope * inp + intercept;
    }

    // Shoulder
    float shoulder_pow = -slope / (((shoulder_x - 1.0) / pow(1.0 - shoulder_x, 2.0)) * (1.0 - shoulder_y));
    return (1.0 - pow(1.0 - (inp - shoulder_x) / (1.0 - shoulder_x), shoulder_pow)) * (1.0 - shoulder_y) + shoulder_y;
}

float flim_dye_mix_factor(float mono, float max_density)
{
    // log2 and map range
    float offset = pow(2.0, flim_sigmoid_log2_min);
    float fac = map_range_clamp(log2(mono + offset), flim_sigmoid_log2_min, flim_sigmoid_log2_max, 0.0, 1.0);

    // Calculate amount of exposure from 0 to 1
    fac = flim_super_sigmoid(fac, flim_sigmoid_toe_x, flim_sigmoid_toe_y, flim_sigmoid_shoulder_x, flim_sigmoid_shoulder_y);

    // Calculate dye density
    fac *= max_density;

    // Mix factor
    fac = pow(2.0, -fac);

    // Clip and return
    return clamp(fac, 0.0, 1.0);
}

vec3 flim_rgb_color_layer(vec3 inp, vec3 sensitivity_tone, vec3 dye_tone, float max_density)
{
    // Normalize
    vec3 sensitivity_tone_norm = sensitivity_tone / rgb_sum(sensitivity_tone);
    vec3 dye_tone_norm = dye_tone / rgb_max(dye_tone);

    // Dye mix factor
    float mono = dot(inp, sensitivity_tone_norm);
    float mixFac = flim_dye_mix_factor(mono, max_density);

    // Dye mixing
    return lerp(dye_tone_norm, vec3(1.0), mixFac);
}

vec3 flim_rgb_develop(vec3 inp, float exposure, float max_density)
{
    // Exposure
    inp *= pow(2.0, exposure);

    // Blue-sensitive layer
    vec3 result = flim_rgb_color_layer(inp, vec3(0, 0, 1), vec3(1, 1, 0), max_density);

    // Green-sensitive layer
    result *= flim_rgb_color_layer(inp, vec3(0, 1, 0), vec3(1, 0, 1), max_density);

    // Red-sensitive layer
    result *= flim_rgb_color_layer(inp, vec3(1, 0, 0), vec3(0, 1, 1), max_density);

    return result;
}

vec3 flim_gamut_extension_mat_row(float primary_hue, float scale, float rotate, float mul)
{
    vec3 result = blender_hsv_to_rgb(vec3(wrap(primary_hue + (rotate / 360.0), 0.0, 1.0), 1.0 / scale, 1.0));
    result /= rgb_sum(result);
    result *= mul;
    return result;
}

mat3 flim_gamut_extension_mat(
    float red_scale,
    float green_scale,
    float blue_scale,
    float red_rot,
    float green_rot,
    float blue_rot,
    float red_mul,
    float green_mul,
    float blue_mul
)
{
    mat3 m;
    m[0] = flim_gamut_extension_mat_row(0.0, red_scale, red_rot, red_mul);
    m[1] = flim_gamut_extension_mat_row(1.0 / 3.0, green_scale, green_rot, green_mul);
    m[2] = flim_gamut_extension_mat_row(2.0 / 3.0, blue_scale, blue_rot, blue_mul);
    return m;
}

vec3 negative_and_print(vec3 inp, vec3 backlight_ext)
{
    // Develop Negative
    inp = flim_rgb_develop(inp, flim_negative_film_exposure, flim_negative_film_density);

    // Backlight
    inp *= backlight_ext;

    // Develop Print
    inp = flim_rgb_develop(inp, flim_print_film_exposure, flim_print_film_density);

    return inp;
}

bool flim_load_matrices(sampler2D matrix_ch, out mat3 extend_mat, out mat3 extend_mat_inv)
{
    // W == valid_w means the matrices have been calculated
    // in the source buffer.
    const float valid_w = 2.0;

    vec4 row;

    // extend_mat
    {
        row = texelFetch(matrix_ch, ivec2(0, 0), 0);
        if (row.w != valid_w)
            return false;
        extend_mat[0] = row.xyz;

        row = texelFetch(matrix_ch, ivec2(1, 0), 0);
        if (row.w != valid_w)
            return false;
        extend_mat[1] = row.xyz;

        row = texelFetch(matrix_ch, ivec2(2, 0), 0);
        if (row.w != valid_w)
            return false;
        extend_mat[2] = row.xyz;
    }

    // extend_mat_inv
    {
        row = texelFetch(matrix_ch, ivec2(3, 0), 0);
        if (row.w != valid_w)
            return false;
        extend_mat_inv[0] = row.xyz;

        row = texelFetch(matrix_ch, ivec2(4, 0), 0);
        if (row.w != valid_w)
            return false;
        extend_mat_inv[1] = row.xyz;

        row = texelFetch(matrix_ch, ivec2(5, 0), 0);
        if (row.w != valid_w)
            return false;
        extend_mat_inv[2] = row.xyz;
    }

    return true;
}

// -------------------------------

// matrix_ch: Which channel provides the buffer
// containing gamut extension matrices?
vec3 flim_transform(vec3 inp, float exposure, sampler2D matrix_ch)
{
    // Eliminate negative values
    inp = max(inp, 0.0);

    // Pre-Exposure
    inp *= pow(2.0, flim_pre_exposure + exposure);

    // Clip very large values for float precision reasons
    inp = min(inp, 5000.0);

    // Gamut Extension Matrix (Linear BT.709)
    mat3 extend_mat, extend_mat_inv;
    if (!flim_load_matrices(matrix_ch, extend_mat, extend_mat_inv))
    {
        return vec3(1, 0, 1);
    }

    // Backlight (extended gamut)
    vec3 backlight_ext = flim_print_backlight * extend_mat;

    // Upper limit in the print (highlight cap)
    const float big = 10000000.0;
    vec3 white_cap = negative_and_print(vec3(big, big, big), backlight_ext);

    // Pre-Formation Filter
    inp = lerp(inp, inp * flim_pre_formation_filter, flim_pre_formation_filter_strength);

    // Convert to extended gamut
    inp *= extend_mat;

    // Negative & Print
    inp = negative_and_print(inp, backlight_ext);

    // Convert from extended gamut
    inp *= extend_mat_inv;

    // Eliminate negative values
    inp = max(inp, 0.0);

    // White cap
    inp /= white_cap;

    // Black cap
    if (flim_black_point == -1.0) // -1 = auto
    {
        vec3 black_cap = negative_and_print(vec3(0.0), backlight_ext);
        black_cap /= white_cap;
        inp = rgb_uniform_offset(inp, rgb_avg(black_cap) * 1000.0, 0.0);
    }
    else
    {
        inp = rgb_uniform_offset(inp, flim_black_point, 0.0);
    }

    // Post-Formation Filter
    inp = lerp(inp, inp * flim_post_formation_filter, flim_post_formation_filter_strength);

    // Clip
    inp = clamp(inp, 0.0, 1.0);

    // Midtone Saturation
    float mono = rgb_avg(inp);
    float mixFac = (mono < 0.5) ? map_range_clamp(mono, 0.05, 0.5, 0.0, 1.0) : map_range_clamp(mono, 0.5, 0.95, 1.0, 0.0);
    inp = lerp(inp, blender_hue_sat(inp, 0.5, flim_midtone_saturation, 1.0), mixFac);

    // Clip
    inp = clamp(inp, 0.0, 1.0);

    // OETF (Gamma 2.2)
    inp = pow(inp, vec3(1.0 / 2.2));

    return inp;
}

/***********************************

End of flim's implementation for Shadertoy Unit: Common

***********************************/

