        muv.x = cos(t * 1.23 + 3.0);
        muv.y = sin(t * 1.34 + 0.2);
        muv *= 0.4;
    }
    else {
        muv = (iMouse.xy - 0.5*iResolution.xy)/iResolution.y;
    }

    vec3 col = vec3(0);

    vec2 ro = muv*1.;
    if(iFrame == 0){
        ro = vec2(0.4);
    }

    float R = 0.25;
    vec2 c = vec2(cos(t), sin(t)) * 0.2;

    float sd = 1000.0;

    sd = min(sd, length(ro - uv) - 0.02);
    sd = min(sd, abs(length(uv - c) - R) - 0.001);

    vec2 tanPosA;
    vec2 tanPosB;

    bool hasTans;

    if (uv.x < 0.0) {
        if (uv.y > 0.0) {
            hasTans = CircleTangents(
                c,
                R,
                ro,
                tanPosA,
                tanPosB);
        }
        else {
