// Original C# source code here:
// https://github.com/elenzil/tangents/blob/master/tangents/Assets/Scripts/TangentCtlr.cs#L78C39-L102
// thanks to Mike Plotz for suggesting this direction.
bool CircleTangents_Plotz(in vec2 center, in float r, in vec2 p, out vec2 tanPosA, out vec2 tanPosB) {
    p -= center;

    float P = length(p);

    // if p is inside the circle, there ain't no tangents.
    if (P <= r) {
      return false;
    }

    float a = r * r                       / P;    
    float q = r * sqrt((P * P) - (r * r)) / P;

    vec2 pN  = p / P;
    vec2 pNP = vec2(-pN.y, pN.x);
    vec2 va  = pN * a;

    tanPosA = va + pNP * q;
    tanPosB = va - pNP * q;

    tanPosA += center;
    tanPosB += center;

    return true;
}



bool CircleTangents_edg(in vec2 c, in float R, in vec2 p, out vec2 tanPosA, out vec2 tanPosB)
{
  p -= c;
  float t = dot(p,p)/(R*R);
  if(t<1.) return false;
  p = p/t;
  vec2 n = vec2(-p.y,p.x) * sqrt(t-1.);
  tanPosA = p+c+n;
  tanPosB = p+c-n;
  return true;
}



#define pi acos(-1.)
#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))


bool CircleTangents(in vec2 c, in float R, in vec2 p, out vec2 tanPosA, out vec2 tanPosB)
{
    float l = length(c - p);
    float u = sqrt(l*l - R*R);

    float beta = asin(R/l);
    float alpha = pi - pi/2.0 - beta;

    vec2 rd = normalize(c - p);

    rd *= rot(beta);
    tanPosA = p + rd * u;

    rd *= rot(-beta*2.0);
    tanPosB = p + rd * u;

    return true;
}

