float superLogicSnake(vec2 p, float s){
    float r = min(min(p.x,p.y),min(s-p.x-1.0,s-p.y-1.0));
    float h = float(p.y>p.x);
    float l = 4.0*(r+h)*(s-r-h);
    l += sign(0.5-h)*(p.x+p.y-2.0*r);
    return l;
}

