#define exposure 2.0

#define fov 1.0

struct intersection
{
    bool  hit; // Hit
    int   id ; // Triangle ID
    float t  ; // Intersection
    vec3  n  ; // Normal
    vec2  uv ; // UV
};

// triangle intersection from https://iquilezles.org/articles/intersectors (modified)
intersection triIntersect(vec3 ro, vec3 rd, vec3 v0, vec3 v1, vec3 v2)
{
    vec3 v1v0 = v1 - v0;
    vec3 v2v0 = v2 - v0;
    vec3 rov0 = ro - v0;

    vec3 n = cross(v1v0, v2v0);
    vec3 q = cross(rov0, rd  );

    float d = 1.0 / dot(rd, n);

    float u = d * dot(-q, v2v0);
    float v = d * dot( q, v1v0);
    float t = d * dot(-n, rov0);

    if(u < 0.0 || u > 1.0 || v < 0.0 || (u + v) > 1.0)
    {
        t = -1.0;
    }

    intersection output_data;

    output_data.hit = t > 0.0     ;
    output_data.t   = t           ;
    output_data.n   = normalize(n);
    output_data.uv  = vec2(u, v)  ;

    return output_data;
}

bool AABB(vec3 ro, vec3 rd, vec3 b_min, vec3 b_max)
{
    vec3 t1 = (b_min - ro) / rd;
    vec3 t2 = (b_max - ro) / rd;

    vec3 Tmin = min(t1, t2);
    vec3 Tmax = max(t1, t2);

    float tMin = max(max(Tmin.x, Tmin.y), Tmin.z);
    float tMax = min(min(Tmax.x, Tmax.y), Tmax.z);

    return tMin <= tMax && tMin > 0.0;
}

intersection compareIntersection(intersection t, intersection t_)
{
    if(t_.hit)
    {
        if(t.hit)
        {
            intersection output_data;

            output_data.hit = true;

            if(t.t < t_.t)
            {
                /*
                output_data.id = t.id;
                output_data.t  = t.t ;
                output_data.n  = t.n ;
                output_data.uv = t.uv;
                */
                output_data = t;
            }
            else
            {
                /*
                output_data.id = t_.id;
                output_data.t  = t_.t ;
                output_data.n  = t_.n ;
                output_data.uv = t_.uv;
                */
                output_data = t_;
            }

            return output_data;
        }

        return t_;
    }

    return t;
}

intersection trace(vec3 ro, vec3 rd)
{
    const int num_tris = 4;

    /*
    // GL_TRIANGLE_STRIP
    // https://en.wikipedia.org/wiki/Triangle_strip
    // 72 bytes
    const vec3[] vert = vec3[] (
    vec3(-1.0, -1.0,  1.0),
    vec3( 1.0, -1.0,  1.0),
    vec3(-1.0, -1.0, -1.0),
    vec3( 1.0, -1.0, -1.0),
    vec3(-1.0,  1.0, -1.0),
    vec3( 1.0,  1.0, -1.0)
    );
    */

    // 144 bytes
    const vec3[] vert = vec3[] (
    vec3(-1.0, -1.0,  1.0),
    vec3( 1.0, -1.0,  1.0),
    vec3(-1.0, -1.0, -1.0),

    vec3(-1.0, -1.0, -1.0),
    vec3( 1.0, -1.0,  1.0),
    vec3( 1.0, -1.0, -1.0),

    vec3(-1.0, -1.0, -1.0),
    vec3( 1.0, -1.0, -1.0),
    vec3(-1.0,  1.0, -1.0),

    vec3(-1.0,  1.0, -1.0),
    vec3( 1.0, -1.0, -1.0),
    vec3( 1.0,  1.0, -1.0)
    );

    vec3 bound_min = vert[0];
    vec3 bound_max = vert[0];

    for(int i = 1; i < 3 * num_tris; i++)
    {
        bound_min = min(bound_min, vert[i]);
        bound_max = max(bound_max, vert[i]);
    }

    intersection output_data;

    output_data.hit = false    ;
    output_data.id  = -1       ;
    output_data.t   = -1.0     ;
    output_data.n   = vec3(0.0);
    output_data.uv  = vec2(0.0);

    //st_assert(AABB(ro, rd, bound_min, bound_max), 0);

    if( AABB(ro, rd, bound_min, bound_max) )
    {
        for(int i = 0; i < num_tris; i++)
        {
            int index = 3 * i;

            intersection input_data = triIntersect(ro, rd, vert[index + 0], vert[index + 1], vert[index + 2]);

            input_data.id = i;

            output_data = compareIntersection(output_data, input_data);
        }
    }

    return output_data;
}

vec2 rotate(vec2 vec, float rot)
{
    float s = sin(rot), c = cos(rot);
    return vec2(vec.x * c - vec.y * s, vec.x * s + vec.y * c);
}

float hash13(vec3 p3)
{
    p3 *= 0.1031;
    p3  = fract(p3);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract( (p3.x + p3.y) * p3.z );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec2 uv = 2.0 * (fragCoord - 0.5 * iResolution.xy) / max(iResolution.x, iResolution.y);

    vec3 ro = vec3(0.0, 0.0, 4.0);
    vec3 rd = normalize( vec3(fov * uv, -1.0) );

    ro = vec3(rotate(ro.xz, iTime), ro.y).xzy;
    rd = vec3(rotate(rd.xz, iTime), rd.y).xzy;

    intersection t = trace(ro, rd);

    if(t.hit)
    {
        vec3 h = ro + rd * t.t;

        vec3 a = vec3(1.0, 0.0, 1.0);

        if(t.id == 0 || t.id == 1)
        {
            a = mod(floor(4.0*h.x) + (4.0 * h.z), 2.0) < 1.0 ? vec3(0.400, 0.400, 0.400) : vec3(0.800, 0.800, 0.800);
        }
        if(t.id == 2 || t.id == 3)
        {
            a = vec3(0.8);
        }

        vec3 l = vec3(0.0, 0.5, 0.0);

        vec3 c = l - h;

        vec3 d = normalize(c);

        float f = 1.0 / max(dot(c, c), 0.001);

        fragColor.rgb = a * dot(t.n, f * d);
        //fragColor.rgb = vec3(t.uv, 0.0);
    }
    else
    {
        fragColor.rgb = vec3(0.0);
    }

    #ifdef exposure
    fragColor.rgb = clamp(1.0 - exp(-max(fragColor.rgb, 0.0) * exposure), 0.0, 1.0);
    #endif

    fragColor.rgb += hash13( vec3(fragCoord, iFrame) ) / 64.0;
}

