#define S(a, b, t) smoothstep(a,b,t)
float TaperBox(vec2 p, float widthBottom, float widthTop, float yBottom, float yTop, float blur){
    float m = S(-blur, blur, p.y-yBottom);
    m *= S(blur, -blur, p.y-yTop);

    p.x = abs(p.x);

    float width = mix(widthBottom, widthTop, (p.y-yBottom)/(yTop-yBottom));

    m *= S(blur, -blur, p.x-width);
    return m;
}

vec4 Tree(vec2 uv, float x, float y, vec3 col, float blur){
    uv -= vec2(x, y);

    float m = TaperBox(uv, 0.03, 0.03, 0.0, 0.25, blur); //trunk
    m += TaperBox(uv, 0.2, 0.1, 0.25, 0.5, blur); //canopy 1
    m += TaperBox(uv, 0.15, 0.05, 0.5, 0.75, blur); //canopy 2
    m += TaperBox(uv, 0.1, 0.0, 0.75, 1.0, blur); //top


    float shadow = TaperBox(uv-vec2(0.25, 0.0), 0.1, 0.5, 0.15, 0.25, blur);
    shadow += TaperBox(uv-vec2(0.25, 0.0), 0.1, 0.5, 0.45, 0.5, blur);
    shadow += TaperBox(uv-vec2(0.25, 0.0), 0.1, 0.5, 0.70, 0.75, blur);

    col -= shadow * 0.5;
    //m = 1.0;
    return vec4(col, m);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
