{    
    // Parameters.

    vec3 color = vec3(195.0 / 255.0, 84.0 / 255.0, 205.0 / 255.0) * 3.0;
    float strength = 10.0;
    float speed = 0.2;
    float brightness = 1.5;
    float cellDensity = 6.0;

    // Shader.

    vec2 twirlResult;
    float voronoiResult;
    float voronoiCells;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

