    float textMask = (1.0-indexText(uv*vec2(15.0,-15.0)+vec2(cos(uv.y*5.0+iTime*2.0), sin(uv.x*5.0+iTime*2.0))));
    float transition = smoothstep(0.0, 0.5, mod(iTime, 5.0));

    // Output to screen
    vec4 color = (transition*color1
    +(1.0-transition)*color3);
    vec4 textColor = 0.4*(transition*color3
    +(1.0-transition)*color2);
    fragColor = textMask*color+(1.0-textMask)*textColor;
}

