/*
This performance test aims to compare cubemapping implementations.

The test works by measuring frames per second (FPS) on Shadertoy. To 
ensure valid results, we repeat the test function with varying values, 
accumulating their results into a final image.

Steps:
- Increase REPEAT until the FPS are below your monitors refreshrate 
    (probably somewhere around 60-144 fps)
- Modify TEST value to switch between test cases
- The highest FPS indicates the fastest implementation
- Congratulate yourself for doing SCIENCE

While it's possible to improve Mips using derivatives, I have not 
implemented this optimization in these versions, cubemapping inherently 
faces challenges with mapping on at least six edges. Therefore, I have 
not implemented Mip optimization for these implementations.
*/

// Number of repeats of the test, 4000 should be enough for RTX3080
#define REPEAT 1

// switch between the test cases
// 0 = Cubemap using if ~ 81 fps
// 1 = Folded Cubemap using if ~ 44 fps
// 2 = Cubemap using a Mask ~ 61 fps
// 3 = Cubemap using a Mask v2 ~ 62 fps
// 4 = folded Cubemap using a Mask  ~ 37 fps
#define TEST 1

// smallest float to avoid float errors
#define TINY (1.0 / float(0xffffffffU))

// Default Cubemap using if from IG https://www.shadertoy.com/view/ltl3D8
vec2 CubemapIf(vec3 uvw) {
    // intersect cube
    vec3 n = abs(uvw);
    vec3 v = (n.x>n.y && n.x>n.z) ? uvw.xyz: 
             (n.y>n.z) ?            uvw.yzx:
                                    uvw.zxy;
    // project into face
    vec2 uv = v.yz/v.x;

    // Cass Everitt's piecewise quadratic warp 
    // https://github.com/casseveritt/projection/blob/master/envmap.h (Ctrl+F pinch)
    vec2 distort = (1.45109572583 - 0.451095725826*abs(uv));
    uv *= distort;

    // rescale to 0-1 range
    return 0.5+0.5*uv;
}

// Cubemap using if, that is folded so all but 6 edges align
vec2 CubemapFoldedIf(vec3 uvw) {
    // bitmask for flipping some sides
    vec3 sUVW = sign(uvw);

    // intersect cube
    vec3 n = abs(uvw);
    vec3 v = (n.x>n.y && n.x>n.z) ? vec3(uvw.x, uvw.y, sUVW.x * uvw.z): 
             (n.y>n.z) ?            vec3(uvw.y, -uvw.x, sUVW.y * uvw.z):
                                    vec3(uvw.z, sUVW.z * uvw.x, uvw.y);

    // edge distance
    //float eDist = clamp(-(n.z - max(n.x, n.y)), 0., 1.) + 
    //              clamp(n.z - max(n.x, uvw.y), 0., 1.);

    // project into face
    vec2 uv = v.yz/v.x;

    // Cass Everitt's piecewise quadratic warp 
    // https://github.com/casseveritt/projection/blob/master/envmap.h (Ctrl+F pinch)
    vec2 distort = (1.45109572583 - 0.451095725826*abs(uv));
    uv *= distort;

    // rescale to 0-1 range
    return 0.5+0.5*uv;
}

// Cubemap using if, that is folded so all but 6 edges align
vec2 CubemapMostlyFoldedIf(vec3 uvw) {
    // bitmask for flipping some sides
    vec3 sUVW = sign(uvw);

    // intersect cube
    vec3 n = abs(uvw);
    vec3 v = (n.x>n.y && n.x>n.z) ? vec3(uvw.x, uvw.y, sUVW.x * uvw.z): 
             (n.y>n.z) ?            vec3(uvw.y, -uvw.x, sUVW.y * uvw.z):
                                    vec3(uvw.z, uvw.x, uvw.y);

    // edge distance
    //float eDist = clamp(-(n.z - max(n.x, n.y)), 0., 1.) + 
    //              clamp(n.z - max(n.x, uvw.y), 0., 1.);

    // project into face
    vec2 uv = v.yz/v.x;

    // Cass Everitt's piecewise quadratic warp 
    // https://github.com/casseveritt/projection/blob/master/envmap.h (Ctrl+F pinch)
    vec2 distort = (1.45109572583 - 0.451095725826*abs(uv));
    uv *= distort;

    // rescale to 0-1 range
    return 0.5+0.5*uv;
}

// Cubemap using masks instead of If
vec2 CubemapMask(vec3 uvw) {
    // Cube Masks
    vec3 aUVW = abs(uvw);
    vec3 mask = vec3(aUVW.x - max(aUVW.y, aUVW.z),
                     aUVW.y - max(aUVW.x, aUVW.z),
                     aUVW.z - max(aUVW.x, aUVW.y));
    mask = clamp(mask, 0., 1.);
    //float eDist = max(mask.x, max(mask.y, mask.z)); // edge distance
    vec3 bitMask = step(TINY,mask);

    // project into face without if
    vec2 uv = vec2((uvw.yz * bitMask.x)/uvw.x + 
                   (uvw.zx * bitMask.y)/uvw.y + 
                   (uvw.xy * bitMask.z)/uvw.z);

    // Cass Everitt's piecewise quadratic warp 
    // https://github.com/casseveritt/projection/blob/master/envmap.h (Ctrl+F pinch)
    vec2 distort = (1.45109572583 - 0.451095725826*abs(uv));
    uv *= distort;

    // rescale to 0-1 range
    return 0.5+0.5*uv;
}

// Cubemap using masks instead of If
vec2 CubemapMaskv2(vec3 uvw) {
    // Cube Masks
    vec3 aUVW = abs(uvw);
    vec3 mask = vec3(max(aUVW.y, aUVW.z),
                     max(aUVW.x, aUVW.z),
                     max(aUVW.x, aUVW.y));
    //float eDist = max(mask.x, max(mask.y, mask.z)); // edge distance
    vec3 bitMask = step(mask,aUVW);

    // project into face without if
    vec2 uv = vec2((uvw.yz * bitMask.x)/uvw.x + 
                   (uvw.zx * bitMask.y)/uvw.y + 
                   (uvw.xy * bitMask.z)/uvw.z);

    // Cass Everitt's piecewise quadratic warp 
    // https://github.com/casseveritt/projection/blob/master/envmap.h (Ctrl+F pinch)
    vec2 distort = (1.45109572583 - 0.451095725826*abs(uv));
    uv *= distort;

    // rescale to 0-1 range
    return 0.5+0.5*uv;
}

// Cubemap using masks instead of If, that is folded so all but 6 edges align
vec2 CubemapFoldedMask(vec3 uvw) {
    // Cube Masks
    vec3 aUVW = abs(uvw);
    vec3 mask = vec3(aUVW.x - max(aUVW.y, aUVW.z),
                     aUVW.y - max(aUVW.x, aUVW.z),
                     aUVW.z - max(aUVW.x, aUVW.y));

    // edge distance
    //float eDist = clamp(-mask.z, 0., 1.) + 
    //              clamp(aUVW.z - max(aUVW.x, uvw.y), 0., 1.);

    mask = clamp(mask, 0., 1.);
    vec3 bitMask = step(TINY,mask);

    // bitmask for flipping some sides
    vec3 sUVW = sign(uvw);

    // project into face without if
    vec2 uv = vec2((vec2(uvw.y, sUVW * uvw.z) * bitMask.x)/uvw.x + 
                   (vec2(-uvw.x, sUVW.y * uvw.z) * bitMask.y)/uvw.y + 
                   (vec2(sUVW.z * uvw.x, uvw.y) * bitMask.z)/uvw.z);

    // Cass Everitt's piecewise quadratic warp 
    // https://github.com/casseveritt/projection/blob/master/envmap.h (Ctrl+F pinch)
    vec2 distort = (1.45109572583 - 0.451095725826*abs(uv));
    uv *= distort;

    // rescale to 0-1 range
    return 0.5+0.5*uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 dir = sphereNormals(uv);

    // if you get around 144 fps use a higher repeat value
    vec2 test;
    for(int i=0; i<REPEAT; i++) {
        vec3 aDIR = dir + (float(i)/float(REPEAT)); // permutation each iteration

        // use which ever you wish to test
        #if TEST == 0
            test += CubemapIf(aDIR);
        #elif TEST == 1
            test += CubemapFoldedIf(aDIR);
        #elif TEST == 2
            test += CubemapMask(aDIR);
        #elif TEST == 3
            test += CubemapMaskv2(aDIR);
        #else
            test += CubemapFoldedMask(aDIR);
        #endif   
    }

    vec4 col = texture(iChannel0, test);

    // Output to screen
    fragColor = vec4(col);
}

