#define PI 3.1415926536

// sphere normals form morgan3d https://www.shadertoy.com/view/4dsGD2
vec3 sphereNormals(vec2 uv) 
{
    float theta = (1.0 - uv.y) * PI;
	float phi   = uv.x * PI * 2.0;

    float sinTheta = sin(theta);    

    // Equation from http://graphicscodex.com  [sphry]
	vec3 dir = vec3(sinTheta * sin(phi),
                    cos(theta), 
                    sinTheta * cos(phi));
    return dir;
}

