/*
This performance test aims to compare Height to Normal implementations. 

The test works by measuring frames per second (FPS) on Shadertoy. To 
ensure valid results, we repeat the test function with varying values, 
accumulating their results into a final image.

Steps:
- Increase REPEAT until the FPS are below your monitors refreshrate 
    (probably somewhere around 60-144 fps)
- Modify TEST value to switch between test cases
- The highest FPS indicates the fastest implementation
- Congratulate yourself for doing SCIENCE
*/

// Number of repeats of the test, 3000 should be enough for RTX3080
#define REPEAT 1

// switch between the test cases
// 0 = NoxWings normal gen using derivatives ~ 108 fps
// 1 = NoxWings normal gen using 3 texture samples ~ 40 fps
// 2 = dmmn also using 3 texture samples ~ 42 fps
#define TEST 0

// NoxWings normal gen using derivatives
// https://www.shadertoy.com/view/3sSSW1
vec3 HeightToNormalDerivative(sampler2D heightMap, vec2 uv, float strength) {
    float height = texture(heightMap, uv).r;
    vec2 xy = -vec2(dFdx(height), dFdy(height)) * (iResolution.xy/iChannelResolution[0].xy);

    return normalize(vec3(xy*strength, 1.));
}

// NoxWings normal gen using 3 texture samples
// https://www.shadertoy.com/view/3sSSW1
vec3 HeightToNormal3Sample(sampler2D heightMap, vec2 uv, float strength) {
    vec2 s = 1.0/iChannelResolution[0].xy;

    float textureOffset = 1.0;

    float p = texture(heightMap, uv).x;
    float h1 = texture(heightMap, uv + s * vec2(textureOffset,0)).x;
    float v1 = texture(heightMap, uv + s * vec2(0,textureOffset)).x;

  	vec2 xy = (p - vec2(h1, v1));

    return normalize(vec3(xy*strength, 1.));
}

// dmmn also using 3 texture samples
// https://www.shadertoy.com/view/MsScRt
vec3 HeightToNormal3Sample2(sampler2D heightMap, vec2 uv, float strength) {
  vec2 s = 1. / iResolution.xy;

  float height = texture(heightMap, uv).x;

  vec2 dxy = height - vec2(
      texture(heightMap, uv + vec2(s.x, 0.)).x, 
      texture(heightMap, uv + vec2(0., s.y)).x
  );

  vec2 xy = dxy / s;
  return normalize(vec3(xy*strength, 1.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // if you get around 144 fps use a higher repeat value
    vec3 test;
    for(int i=0; i<REPEAT; i++) {
        float mut = (float(i)/float(REPEAT)); // permutation each iteration

        // use which ever you wish to test
        #if TEST == 0
            test += HeightToNormalDerivative(iChannel0, uv + mut, 50.);
        #elif TEST == 1
            test += HeightToNormal3Sample(iChannel0, uv + mut, 50.);
        #else
            test += HeightToNormal3Sample2(iChannel0, uv + mut, 0.1);
        #endif
    }

    // Output to screen
    fragColor = vec4((test*.5+.5), 1.);
}

