// make square tiling of barycentric weights
vec3 baryWeights(vec2 uv) {
    // make individual weights for pattern
    float weightA = min(uv.x, uv.y);
    float weightB = min(1.-uv.x, 1.-uv.y);
    float weightC = 1. - weightA - weightB;

    // Join weights together
    return vec3(weightA, weightB, weightC);
}

// Linearly increases contrast of Alpha
// See: https://www.desmos.com/calculator/hs1nsjb32q
float straightContrast(float alpha, float contrast) {
    return clamp(contrast * (alpha - 0.5) + 0.5, 0., 1.);
}

