// "Mandelbox-Faced Cube" by dr2 - 2023
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

/*
  No. 10 in "Mandelbox" series; others listed in "Compleat Mandelbox 2" (cllfD2).
*/

#define AA  1   // (= 0/1) optional antialiasing

#define VAR_ZERO min (nFrame, 0)

float PrTorusDf (vec3 p, float ri, float rc);
float Maxv2 (vec2 p);
float Minv2 (vec2 p);
float Maxv3 (vec3 p);
float Minv3 (vec3 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 HsvToRgb (vec3 c);
float Fbm2 (vec2 p);

vec3 ltDir, ltDirEx, pathMid;
vec2 qBlk;
float dstFar, tCur, pathRad, cFace;
int nFrame;
const float pi = 3.1415927;

float ObjDf (vec3 p)
{
  vec4 q4, q4o;
  vec3 q;
  float d;
  const float mScale = 2.8;
  const int nIter = 12;
  q = p;
  q4o = vec4 (6. * (fract (q / 6. + 0.5) - 0.5), 1.);
  q4 = q4o;
  for (int j = 0; j < nIter; j ++) {
    q4.xyz = 2. * clamp (q4.xyz, -1., 1.) - q4.xyz;
    q4 = mScale * q4 / clamp (dot (q4.xyz, q4.xyz), 0.25, 1.) + q4o;
  }
  d = length (q4.xyz) / q4.w;
  d = max (d, - PrTorusDf ((p - pathMid).xzy, 0.02, pathRad));
  return d;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, h, s, sLo, sHi, eps;
  eps = 0.0005;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = VAR_ZERO; j < 240; j ++) {
    h = ObjDf (ro + s * rd);
    if (h < eps || s > dstFar) {
      sHi = s;
      break;
    }
    sLo = s;
    s += h;
  }
  if (h < eps) {
    for (int j = VAR_ZERO; j < 6; j ++) {
      s = 0.5 * (sLo + sHi);
      if (ObjDf (ro + s * rd) > eps) sLo = s;
      else sHi = s;
    }
    dHit = sHi;
  }
  return dHit;
}

vec4 ObjNfL (vec3 p)
{
  vec4 v;
  vec2 e;
  float s;
  e = 1e-4 * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  s = dot (v, vec4 (1.));
  return vec4 (normalize (2. * v.yzw - s), (s - 2. * v.x - 4. * ObjDf (p)) / (e.x * e.x));
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 4. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 vn4;
  vec3 col, vn;
  float dstObj, att;
  dstObj = ObjRay (ro, rd);
  col = vec3 (0.);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn4 = ObjNfL (ro);
    vn = vn4.xyz;
    att = exp2 (- 8. * max (dstObj - 4., 0.));
    col = att * HsvToRgb (vec3 (cFace / 10., 0.2, 1.)) *
       (0.2 + 0.8 * step (0.9, abs (vn4.w) / 256.)) * (0.3 + 0.7 * max (dot (vn, ltDir), 0.));
  }
  if (dstObj >= dstFar || Maxv3 (col) < 0.01) col = 4. * StarPat (rd, 64.);
  return clamp (col, 0., 1.);
}

vec3 TrkPath (float t)
{
  return pathMid + vec3 (Rot2D (vec2 (0., pathRad), 2. * pi * t / pathRad), 0.).xzy;
}

void ExMain (out vec3 fCol, in vec2 fCoord)
{
  mat3 vuMat;
  vec3 ro, rd, vd;
  float el, az, zmFac, vuTr;
  vuTr = 0.02 * tCur + 0.3 * cFace;
  pathRad = mix (1.5, 2.5, 0.5 + 0.5 * sin (0.03 * pi * vuTr));
  pathMid = vec3 (0., 0.7 * sin (0.05 * pi * vuTr), 0.);
  ro = TrkPath (vuTr);
  vd = normalize (TrkPath (vuTr + 0.1) - ro);
  az = atan (vd.z, - vd.x) - 0.5 * pi;
  el = 0.;
  vuMat = StdVuMat (el, az);
  zmFac = 15.;
  rd = normalize (vec3 (fCoord, zmFac));
  rd.xy = Rot2D (rd.xy, 0.5 * pi * vuTr);
  rd = vuMat * rd;
  ltDir = vuMat * normalize (vec3 (0.7, 1., -1.));
  fCol = ShowScene (ro, rd);
}

vec4 BlkHit (vec3 ro, vec3 rd, float bSize)
{
  vec3 v, tm, tp, u, vn;
  float dMin, dn, df;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  dMin = dstFar;
  if (df > 0. && dn < df) {
    dMin = dn;
    vn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    u = (v + dn) * rd;
    qBlk = vec2 (dot (u.zxy, vn), dot (u.yzx, vn)) / bSize;
  }
  return vec4 (dMin, vn);
}

vec3 ExShowScene (vec3 ro, vec3 rd)
{
  vec4 db4;
  vec3 col, vn;
  float dstObj;
  db4 = BlkHit (ro, rd, 3.);
  dstObj = db4.x;
  if (dstObj < dstFar) {
    vn = db4.yzw;
    cFace = dot (vn, vec3 (4., 2., 1.)) + 4.;
    if (Maxv2 (abs (qBlk)) < 0.97) {
      ExMain (col, qBlk);
    } else {
      col = vec3 (0.5) * (0.2 + 0.8 * max (dot (vn, ltDirEx), 0.)) +
         0.3 * pow (max (dot (ltDirEx, reflect (rd, vn)), 0.), 32.);
    }
  } else {
    col = StarPat (rd, 16.);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.;
  zmFac = 4.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
    zmFac += 2. * abs (az);
  } else {
    az += 0.03 * pi * tCur;
    el -= 0.07 * pi * tCur;
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -22.);
  dstFar = 100.;
  ltDirEx = vuMat * normalize (vec3 (0.5, 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ((length (uv) > 0.99) ? vec3 (0.82) : ExShowScene (ro, rd));
  }
  fragColor = vec4 (col, 1.);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (mod (dot (p, cHashVA2) + vec2 (0., cHashVA2.x), 2. * pi)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

int maxSteps = 512;
float maxDepth = 500.;
float minDist = 0.001;
vec3 lightDir = vec3(.5, -.4, 1.);

const float aoScale = 0.8;

void mainImage( out vec4 O, in vec2 U ){

    lightDir = normalize(lightDir);

    vec2 r = iResolution.xy;
    vec2 uv = (2.*U - r)/r.y;
    vec2 muv = iMouse.xy == vec2(0)?vec2(0):(2.*iMouse.xy - r)/r.y;

    float depth = 0.0;
    vec3 camDir = vec3(-sin(-muv.x)*cos(muv.y), cos(-muv.x)*cos(muv.y), sin(muv.y));
    vec3 o = vec3(0, 0, -.5) - camDir*6.;
    vec3 camRight = normalize(cross(camDir, vec3(0, 0, 1)));
    vec3 camUp = (cross(camRight, camDir));
    vec3 dirV = normalize(camDir*2. + camRight * uv.x + camUp * uv.y);
    vec3 p;

    for (int i = 0; i++ < maxSteps;){

        p = o + dirV * depth;
        float d = DF(p).x;

        depth += d;

        if (depth > maxDepth){
            depth = maxDepth;
            break;
        }

        if (d < minDist){
            break;
        }
    }

    vec3 norma = norm(p, depth * 1e-4, 0);
    vec3 normb = norm(p, depth * 1e-4, 1);

    O*=0.;

    float light = 1.;

    float f = smoothstep(0., 1.,-dot(norma, normb)*.5+.5);

    float ao = sqrt(DF(p).y);

    O = vec4(.7);
    light *= min(1., ao/f / aoScale);
    light *= (dot(norma, lightDir) * .3 + .5) / (depth*.2);
    O.xyz *= light;    

    O = pow(O, vec4(1./2.4));
}

