vec3 DF(vec3 p){
    float one   = 1e20;
    float two   = 1e20;
    float three = 1e20;
    float x = 0.;
    vec3 d = p;


    // Make this procedural iterations


    for (float i = 0.; i++<5.;){
        d = p;
        d.y -= i;
        d.xy *= rot(i/2.);
        d.yz *= rot(i/5.);
