// A collection of frying pans to use for silkscreening
// the cover of a cookbook.
//
// Pretty gruesome code.
// In this one I tried out using Globals for things like
// current Pixel Coordinate, current Color, etc,
// in order to simplify the function signatures.
// But I won't be doing it that way again.

#define RES    (iResolution.xy)
#define MINRES (min(RES.x, RES.y))

      float zoom = 0.8;
      float eps;
      vec2  P;
      vec2  Q;
      float C;
      float W;

const vec2 vX = vec2(1.0, 0.0);
const vec2 vY = vX.yx;
const vec2 v0 = vX.yy;
const vec2 v1 = vX.xx;

vec2 grad(in float value) {
    return vec2(dFdx(value), dFdy(value));
}
float gradmag(in float value) {
    return length(grad(value));
}

void fill(in float d, in vec2 col) {
    float b = smoothstep(eps, -eps, d);
    C = mix(C, col.x, col.y * b);
}

void outl(in float d, in vec2 col) {
          d = abs(d) - (W * eps);
//        d = 2e-3 * d / gradmag(d);
    float b = smoothstep(eps, -eps, d);
    C = mix(C, col.x, col.y * b);
}

float sdEllipse_2(in vec2 cen, in vec2 ab) { return sdEllipse(cen - P, ab); }

float sdCircle(in vec2 cen, in float rad) { return distance(P, cen) - rad; }

// https://iquilezles.org/articles/distfunctions2d
float sdBox(in vec2 cen, in vec2 b ) {    vec2 d = abs(cen - P)-b;    return length(max(d,0.0)) + min(max(d.x,d.y),0.0); }

float sdPan(
    float rad,
    float handle_length,
    bool lipL,
    bool lipR,
    bool circ,
    bool hole
    ) {
    float d = 1e9;

    vec2 QQ = Q;
    QQ.x += (rad + handle_length) / 2.0;
    QQ.x -= 0.5;

    float ellipse_offset = rad + handle_length * 1.1;
    float ellipse_length = handle_length * 0.3;

    if (hole) {
        QQ.x -= ellipse_offset + ellipse_length * 0.5;
        QQ *= rot2_deg(5.0 * sin(iTime * 10.0 / rad));
        QQ.x += ellipse_offset + ellipse_length * 0.5;
    }

    // basic circle of the pan  
    P = QQ;
    d = min (d, sdCircle(v0, rad));

    // ears of the pan
    if (lipL) {
        d = smin(d, sdCircle(vec2(-0.02, -rad), rad / 0.7 * 0.1), 2e-1);
    }
    if (lipR) {
        d = smin(d, sdCircle(vec2(-0.02,  rad), rad / 0.7 * 0.1), 2e-1);
    }

    // pan handle
    P = QQ;
    // taper space a little
    P.y = mix(P.y, P.y * 0.8, P.x);

    d = smin(d, sdBox(vX * (rad + handle_length / 2.0), vec2(handle_length, 0.0)) - 0.1, 1e-1);

    // outline
//  d = min(d, abs(d - 6.0 * W * eps) - 2.0 * W * eps);

    // cut an ellipse in the end of the handle
    if (hole) {
        P.y = mix(P.y * 1.4, P.y, (P.x - ellipse_offset) * 4.0);

        d = max(d, -sdEllipse_2(vX * ellipse_offset, vec2(ellipse_length, 0.05)));
    }

    // cut a circle in the pan to indicate the bottom
    if (circ) {
        P = QQ;
        d = max (d, -(abs(sdCircle(v0, rad * 0.8)) - W * eps));
    }

    return d;
}


void mainImage( out vec4 RGBA, in vec2 XY )
{
    zoom = 0.445;
    // normalized coordinate
    float cellSize = 400.0;
  //  XY += RES / MINRES * iTime * 50.0;
    XY.y -= iTime * 50.0;

    // color
    C    = 1.0;

    float d = 1e9;

    // epsilon for AA
    eps  = 2.0 / cellSize / zoom;

    // line width (in eps's)
    W    = 2.0;

    for (uint pass = 0u; pass <= 1u; ++pass) {
        if (pass == 1u) {
            XY += cellSize / 2.0;
        }

        vec2 xy = mod(XY, cellSize);
        Q = (xy * 2.0 - cellSize) / cellSize / zoom;
        Q *= rot2_deg(90.0);
        Q.x += 0.1;

        vec2  cellIndx = vec2(XY / cellSize);
        uvec2 cellIndxU = uvec2(abs(cellIndx));

        uint S = pass * 100u;

        float hash1 = whangHashNoise(cellIndxU.x, cellIndxU.y, S++);
        float hash2 = whangHashNoise(cellIndxU.x, cellIndxU.y, S++);
        float hash3 = whangHashNoise(cellIndxU.x, cellIndxU.y, S++);
        float hash4 = whangHashNoise(cellIndxU.x, cellIndxU.y, S++);
        float hash5 = whangHashNoise(cellIndxU.x, cellIndxU.y, S++);
        float hash6 = whangHashNoise(cellIndxU.x, cellIndxU.y, S++);

        float panSize = 1.0 + 0.8 * (hash1 - 0.5);
        float panHndl = panSize * (0.5 + 0.3 * hash2);
        bool  lipL    = hash3 < 0.5;
        bool  lipR    = hash4 < 0.5;
        bool  circ    = lipL || lipR || hash5 < 0.8;
        bool  hole    = hash6 < 0.9;

        d = min(d, sdPan(panSize, panHndl, lipL, lipR, circ, hole));
    }

    fill(d, vec2(0.0, 1.0));
    outl(d, vec2(0.5, 0.0));

    vec3 BG = vec3(1.0, 1.0, 0.95);
    vec3 FG = vec3(0.1, 0.1, 0.20);

    RGBA.rgb = mix(BG, FG, 1.0 - C);
    RGBA.a   = 1.0;

    // view distance field
    // RGBA.rgb = mix(RGBA.rgb, vec3(0.4, 0.3, 0.1), sin(d * 100.0) * 0.5 + 0.5);
}

