vec2 noise(vec2 uv){
    vec2 v0=random(floor(uv));
    vec2 v1=random(floor(uv)+vec2(1,0));
    vec2 v2=random(floor(uv)+vec2(1,1));
    vec2 v3=random(floor(uv)+vec2(0,1));
    vec2 dtt=uv-floor(uv);
    vec2 x1 =lerp(v0,v1,dtt.x);
    vec2 x2 =lerp(v3,v2,dtt.x);
    vec2 o=lerp(x1,x2,dtt.y);
    //return v2;
    return o;   
}
float noises(vec2 uv,int level){
    float c=0.0;
    float scale=10.0;
    float t=2.0;
    for(int i=0;i<10;i++){
        if(i>level)
            break;
        c+=noise(uv*scale).x/t;
        scale*=2.0;
        t*=2.0;
    }
    return c;
}

