    return normalize( e.xyy*map( pos + e.xyy,t ).x + 
					  e.yyx*map( pos + e.yyx,t ).x + 
					  e.yxy*map( pos + e.yxy,t ).x + 
					  e.xxx*map( pos + e.xxx,t ).x );

}

float calcAO( in vec3 pos, in vec3 nor )
