// V-Drop - Del 19/11/2019 - (Tunnel mix - Enjoy)
// vertical version: https://www.shadertoy.com/view/tdGXWm
#define PI 3.14159

float vDrop(vec2 uv,float t)
{
    uv.x = uv.x*128.0;						// H-Count
    float dx = fract(uv.x);
    uv.x = floor(uv.x);
    uv.y *= 0.05;							// stretch
    float o=sin(uv.x*215.4);				// offset
    float s=cos(uv.x*33.1)*.3 +.7;			// speed
    float trail = mix(95.0,35.0,s);			// trail length
    float yv = fract(uv.y + t*s + o) * trail;
    yv = 1.0/yv;
    yv = smoothstep(0.0,1.0,yv*yv);
    yv = sin(yv*PI)*(s*5.0);
    float d2 = sin(dx*PI);
    return yv*(d2*d2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float d = length(p)+exp(-iTime)*0.9+0.1;
	p = vec2(atan(p.x, p.y) / PI, 2.5 / d);
    if (iMouse.z>0.5)
    	p.y *= 0.5;
    float t =  iTime*0.4;
    vec3 col = vec3(1.55,0.65,.225) * vDrop(p,t);	// red
    col += vec3(0.55,0.75,1.225) * vDrop(p,t+0.33);	// blue
    col += vec3(0.45,1.15,0.425) * vDrop(p,t+0.66);	// green
	fragColor = vec4(col*(d*d), 1.0);
}

#define I iResolution
#define PI 3.1415926
#define T( s ) fract( iTime * s ) * PI * 4.

// smax
float s(float a, float b, float c) {
  float d = clamp(.5 + .5 * (-b + a) / c, 0., 1.);
  return -(mix(-b, -a, d) - c * d * (1. - d));
}

mat2 rot(float a) {
  float s = sin(a), c = cos(a);
  return mat2(c, s, -s, c);
}

float cs( vec3 p ){
    p.xy *= rot( PI * -.25 );
    p.yz *= rot( PI * .25 );
    p.xy *= rot( T( .025 ) );
    p.xy *= rot( p.z * 40. );

    float d = abs( p.x ) - .2;

    d = -min( -d, -( abs( p.y ) - .2 ) );

    p.xy *= 1.5;

    float s2 = abs( p.x ) - .2;
    s2 = min( -s2, -( abs( p.y ) - .2 ) );
    d = max( d, s2 );


    return max( d, abs( p.z ) - 1. );
}

float cd( vec3 p ){
    p.xy *= rot( PI * -.25 );
    p.yz *= rot( PI * .25 );

    p = mod( p, .01 ) - .005;
    return length( p * vec3( 1., .6, 1. ) ) - .001;
}

// sdf
float df(vec3 p) {
    p.xz *= rot( T( .0025 ) );
    p.xy *= -rot( T( .0025 ) );
    p.xz *= rot( PI * .25 );
    p.xy *= rot( PI * .125 );
    float k = 0.;

    for( float i = 0.; i < 7.; i++ ){
        p.xz *= rot( i );
        k += .8;
        p = abs( p ) - .5;
        p.xy *= rot( i * k * ( i - k ) );
        p.xy = abs( p.xy ) - .5;
        p.yz *= rot( i * k * ( i - k ) );
        p.x = abs( p.x ) - k * .121;
    }

    return clamp( s( cs( p ), -cd( p ), .06 ), 0., 1. );
}

// calcNormal (IQ)
vec3 l(in vec3 b) {
  vec2 a = vec2(1, -1) * .5773;
  return normalize(a.xyy * df(b + a.xyy * 5e-4) + a.yyx * df(b + a.yyx * 5e-4) +
                   a.yxy * df(b + a.yxy * 5e-4) + a.xxx * df(b + a.xxx * 5e-4));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u = ( (fragCoord/I.xy - .5) * 2. ) * vec2( I.x / I.y, 1. );

    vec3 c, p, o = vec3( 0., 0., -1. ), r = vec3( u * .1, 1. );
    float t, d, i;
    c += .1;

    for( ; i < 64.; i++ )
        p = o + r * t,
        d = df( p ),
        t += d;

    if( d < 1e-3 )
        c += vec3( clamp(dot( l( p ) , vec3(.4, 1., -.5)), 0.0, 1.0) );

    // Output to screen
    fragColor = vec4(sqrt( c ),1.0);
}

