float f (float x) {
    // Change the input signal here!
    return 10.*smoothstep(0.,1.,x)*asin(.9*cos(x*.25+10.*sin(.02*x)));
}
float df (float x) {
    float e = .1;
    return (f(x+e)-f(x-e))/(2.*e);
}
float ddf (float x) {
    float e = .1;
    return (df(x+e)-df(x-e))/(2.*e);
}


vec2 g (float x) {
    // Change the input signal here!

    return vec2(50.*asin(.9*sin(3.1+2.*x)),
                50.*asin(.9*sin(3.1+1.5*x)))*step(0.,x);
}
vec2 dg (float x) {
    float e = .1;
    return (g(x+e)-g(x-e))/(2.*e);
}
vec2 ddg (float x) {
    float e = .1;
    return (dg(x+e)-dg(x-e))/(2.*e);
}

