// Made an example that uses an input image using the original RodzLabs Code
// Original code by RodzLabs here - https://www.shadertoy.com/view/mt2fRy

const float density = 10.000000000;
const float randomness = 1.000000000;

float in_density(vec2 uv){
    vec4 c=texture(iChannel0,uv);
    return (c.x+c.y+c.z)/3.0;
}

float in_randomness(vec2 uv){
    vec4 c=texture(iChannel1,uv);
    return (c.x+c.y+c.z)/3.0;
}

vec2 rand2(vec2 x) {
    return fract(cos(mod(vec2(dot(x, vec2(13.9898, 8.141)),
						      dot(x, vec2(3.4562, 17.398))), vec2(3.14, 3.14))) * 43758.5);
}

// Based on https://www.shadertoy.com/view/ldl3W8
// The MIT License
// Copyright  2013 Inigo Quilez
vec2 voronoi_cell_offset(int i, int np, vec2 seed, vec2 offset, vec2 size) {
	float rows = round(sqrt(float(np)));
	float rowsize = float(np)/rows;
	float row = floor(float(i)/rowsize);
	float firstcol = ceil(row*float(np)/rows);
	float cols = ceil((row+1.0)*float(np)/rows) - firstcol;
	vec2 c1 = vec2((float(i) - firstcol)/cols, float(row)/float(rows));
	vec2 c2 = vec2(((float(i+1) - firstcol))/cols, (row+1.0)/rows);
	return c1+(randomness*in_randomness(offset+0.5*(c1+c2)*size)*(seed-vec2(0.5))+vec2(0.5))*(c2-c1);
}


vec4 variable_voronoi(vec2 x, vec2 size, vec2 stretch, vec2 seed) {
	x *= size;
	vec2 n = floor(x);
	vec2 f = fract(x);

	vec2 mg, mr, mc;
	float md = 8.0;
	for (int j=-1; j<=1; j++)
	for (int i=-1; i<=1; i++) {
		vec2 g = vec2(float(i),float(j));
		vec2 s = seed + mod(n + g + size, size);
		int np = max(1, int(in_density(fract((n + g + vec2(0.5)) / size))*density));
		for (int p = 0; p < np; ++p) {
			s = rand2(s);
			vec2 o = voronoi_cell_offset(p, np, s, fract((n + g)/size), vec2(1.0)/size);
			vec2 c = g + o;
			vec2 r = c - f;
			vec2 rr = r*stretch;
			float d = dot(rr,rr);

			if (d<md) {
				mc = c;
				md = d;
				mr = r;
				mg = g;
			}
		}
	}

	md = 8.0;
	for (int j=-2; j<=2; j++)
	for (int i=-2; i<=2; i++) {
		vec2 g = mg + vec2(float(i),float(j));
		vec2 s = seed + mod(n + g + size, size);
		int np = max(1, int(in_density(fract((n + g + vec2(0.5)) / size))*density));
		for (int p = 0; p < np; ++p) {
			s = rand2(s);
			vec2 o = voronoi_cell_offset(p, np, s, fract((n + g)/size), vec2(1.0)/size);
			vec2 r = g + o - f;
			vec2 rr = (mr-r)*stretch;
			if (dot(rr,rr)>0.00001)
				md = min(md, dot(0.5*(mr+r)*stretch, normalize((r-mr)*stretch)));
		}
	}
	return vec4(mc+n, length((x-(mc+n))*stretch), md);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv=fragCoord/iResolution.xy;

    fragColor = vec4(vec3(variable_voronoi(uv,vec2(32.0,32.0),vec2(1.0,1.0),vec2(0.190681249)).z),1.0);
}

