float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 hash23(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

// gyroid pattern
float gyroid (vec3 p)
{
    return dot(sin(p),cos(p.yzx));
}

// noise
float fbm (vec2 p)
{
    vec3 q = vec3(p, 0.);
    float result = 0., a = .5;
    for (float i = 0.; i < 4.; ++i, a /= 2.)
    {
        q += (result-.5)*.5;
        result += abs(gyroid(q/a)*a);
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    p += iTime * 0.1;

    // animate
    vec2 q = p*.5;
    vec2 cell = floor(q);
    float t = iTime*(.5+pow(hash12(cell), 4.)*2.);
    vec2 anim = vec2(fract(t), floor(t));
    vec2 offset = mix(
        hash23(vec3(cell, anim.y)),
        hash23(vec3(cell, anim.y+1.)),
        smoothstep(.0,1.,anim.x));
    q += (offset-.5)*.1;

    // shape
    vec2 e = vec2(.3,0);
    float diff = abs(fbm(p+e.yx)-fbm(p-e.yx))/e.x;
    float shape = smoothstep(.001,.0, diff-1.);

    // color
    float circle = length(fract(q)-.5);
    vec3 tint = .5+.5*cos(vec3(1,2,3)*5.5+floor(diff-circle*10.)*.5);
    vec3 color = shape * tint;
    color += (1.-circle) * .02 * tint / circle;

    fragColor = vec4(color, 1);
}

