// hash function from "Hash without sine" by Dave_Hoskins - https://www.shadertoy.com/view/4djSRW
float hash(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float blinkLevel(int size, float frameNum){
    if(size == 1){
        return floor(mod(frameNum, 2.));
    }else if(size == 2){
        float num = ceil(mod(frameNum, 4.));
        if(num > 2.){
            return 4. - num;
        }else{
            return num;
        }
    }else{
        return 0.;
    }
}

vec3 blinkColor(int id){
    if(id == 0){
        return vec3(.8,0.,0.);
    }else if(id == 1){
        return vec3(1.,.2,.8);
    }else if(id == 2){
        return vec3(0.,.8,0.);
    }else if(id == 3){
        return vec3(.4,1.,.4);
    }else if(id == 4){
        return vec3(0.,0.,1.);
    }else if(id == 5){
        return vec3(0.,.8,1.);
    }else if(id == 6){
        return vec3(1.,1.,0.);
    }
    return vec3(0.);
}


vec4 blink(vec2 p){
    vec2 pixelUnit = vec2(1.) / iResolution.xy;
    vec2 whRatio = vec2(iResolution.x / iResolution.y,1.);
    vec4 l = vec4(0.);
    float whAvg = (iResolution.x + iResolution.y) / iResolution.y * 90.;
    for (float i = 0.; i < 320.; i += 1.) {
        float z = hash(i*.1);
        l.rgb = blinkColor(int(hash(i*.4) * 7.));
        float offset = hash(i*.5) * 3.;
        int size = int(3.*z*z);
        int depth = (3 - size) * 100;
        float level = blinkLevel(size, iTime / 0.2f + offset) + 1.;
        float invDepth = 1./float(depth);
        vec2 pos = fract(vec2(hash(i*.2), hash(i*.3)) + 100.*iMouse.xy/iResolution.xy*invDepth);
        pos -= mod(pos,pixelUnit) + pixelUnit / 2.;
        vec2 delta = (p - pos) * whRatio;
        float range = abs(delta.x) + abs(delta.y);

        float inRange = (level - 2.) / whAvg;
        float outRange = level / whAvg;

        l.a = step(inRange, range) * step(range, outRange);

        if(l.a > 0.){
            return l;
        }
    }
    return vec4(0.);
}

