>>>>>>>by ThePlaneGuy45<<<<<<<<

credits to Ingo Quilez for their Segment SDF: https://www.shadertoy.com/view/3tdSDj

*/

float sdSegment( in vec2 p, in vec2 a, in vec2 b ) {

    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );

} // From Inigo Quilez (https://www.shadertoy.com/view/3tdSDj)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec2 c = fragCoord - iResolution.xy/2.0;
    c /= iResolution.x;
    c *= 10.0;
    vec2 m = iMouse.xy;
    m.x -= iResolution.x/2.0;
    m /= iResolution.y;
    m *= vec2(10.0, 5.0);

    if(abs(mod(c.x+m.x/m.y, 1.0/m.y)) < 0.01 || abs(mod(c.y, 1.0/m.y)) < 0.01) {

        fragColor = vec4(0.3, 0.3, 0.4, 1.0);

    }

    if(abs(c.x+m.x/m.y) < 0.015) {

        fragColor = vec4(0.7, 0.5, 0.5, 1.0);

    }

    if(abs(c.y) < 0.015) {

        fragColor = vec4(0.5, 0.7, 0.5, 1.0);

    }

    float dist;
    float y;
    float prev;
    float close = 10000.0;
    vec4 fc = fragColor;

    for(float x = -10.0; x < 10.0; x+=STEP) {

        y = f(x*m.y + m.x)/m.y;
        dist = sdSegment(c, vec2(x, y), vec2(x-STEP, prev));
        if(dist<=0.02 && dist<close){close=dist;fragColor=mix(fc, vec4(1.0), 

            smoothstep(1.0, 0.0, close * 50.0)

        );}
        prev = y;

    }

}

