    x /= 2.0;
    float xx = pow(x, x);
    return 2.0 * abs(sin(xx) / pow(2.0, (xx - PI / 2.0) / PI));
case 1:
    return (sin(PI*x)+sin(2.0*x))/2.0;
case 2:
    return x*x;
